INSERT INTO `articles` (`id`, `title`, `category`, `userfile`, `slug`, `pubdate`, `body`, `created`, `modified`) VALUES
(15, 'नेयमार कतै जाँदैनन्ः बार्सिलोना अध्यक्ष', 'sports', '7.PNG', 'Neymar_remain_in-_barca', '2017-07-20', '<p style="margin-bottom: 15px; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;">बार्सिलोनाका फरवार्ड नेयमार जुनियर कतै नजाने क्लबका अध्यक्ष जोसे मारियो बर्तामाउले स्पष्ट पारेका छन्। हालै नेयमार फ्रान्सेली क्लब पेरिस सेन्ट जर्मेनसँग अनुबन्धित हुन सहमत भएको रिपोर्ट प्रकाशित भएको थियो।</p><p style="margin-top: 15px; margin-bottom: 15px; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;">तर बिबिसीले बर्तामाउलाई उधृत गर्दै ब्राजिली खेलाडी नेयमार बार्सिलोना छोडेर अर्को क्लबमा नजाने बताएको छ।</p><p style="margin-top: 15px; margin-bottom: 15px; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;">सन् २०१३ मा ब्राजिली क्लब सान्तोसबाट बार्सिलोनामा आबद्ध भएका थिए। उनले गत अक्टोबारमा २०२१ सम्मका लागि अनुबन्ध लम्ब्याएका थिए।</p><p style="margin-top: 15px; margin-bottom: 15px; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;">यसअघिका समाचारहरुमा फ्रान्सेली क्लब पिएसजी नेयमारका लागि २२ करोड डलरभन्दा बढी रकम तिर्न तयार भएको बताइएको थियो। एजेन्सी</p>', '2017-07-20 19:04:02', '2017-07-20 19:04:02'),
(23, 'माओवादी ओरालो लाग्ने दिन सकिएः प्रचण्ड', 'politics', '', 'maoist-going-down-term-ended-prachanda', '2017-07-19', '<p style="margin-bottom: 15px; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;">नेकपा माओवादी केन्द्रका अध्यक्ष पुष्पकमल दाहाल ‘प्रचण्ड’ले माओवादी पार्टीको ओरालो लाग्ने दिन सकिएको बताएका छन्। बुधबार माओवादी केन्द्र ललितपुर जिल्लाको प्रशिक्षण, सम्मान तथा पार्टी प्रवेश कार्यक्रममा बोल्दै पूर्वप्रधानमन्त्री समेत रहेका अध्यक्ष प्रचण्डले माओवादी सिटका हिसाबले तेस्रो भए पनि मतका हिसाबले दोस्रो हुने क्रममा अगाडि बढेको बताए।</p><div class="nk-breaker uk-margin-large uk-align-right uk-margin-large-left uk-width-medium-3-10" style="width: 199.5px; margin-bottom: 50px; float: right; margin-top: 50px; padding-top: 40px; background-image: url(&quot;../../_img/qote1.png&quot;); background-position: left top; background-size: initial; background-repeat: no-repeat; background-attachment: initial; background-origin: initial; background-clip: initial; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px; margin-left: 50px !important;"><h3 class="uk-h3 uk-margin-remove" style="font-family: &quot;Ek Mukta&quot;, sans-serif; font-weight: 600; color: rgb(17, 17, 17); line-height: 34px; margin: 0px !important;">नक्कली राष्ट्रवादलाई जनतामा भ्रमित गर्ने गरि प्रचार गरिएको भन्दै त्यसलाई चिर्नुपर्ने बताए। उनले भने, ‘हामीले अरु केही गर्नु पर्दैन नक्कली राष्ट्रवादको भ्रम चिरे पुग्छ। जनतालाई हिमाल पहाड तराई जोड्ने माओवादीको सच्चा राष्ट्रवाद देखाए पुग्छ।’</h3></div><p style="margin-top: 15px; margin-bottom: 15px; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;">केही मान्छेहरुले माओवादी पार्टी सकियो भन्दै गलत प्रचार गरेको भन्दै उनले त्यसको जवाफ निर्वाचनले दिएको बताए। ‘केही मान्छेहरु माओवादी सकिएको भन्दै प्रचार गर्थे, चौथो पाँचौ पनि हुँदैन भन्थे तर चुनावले पचहत्तरै जिल्लामा काङ्ग्रेस एमालेसँग टक्कर लिएर तेस्रो हुने माओवादी बाहेक अरु छैनन् भन्ने देखायो’, उनले भने, ‘हामी तेस्रो भयौं, अब पहिलो र दोस्रो हुने ढोका खुलेको छ, चौथो हुने ढोका बन्द भयो।’</p><p style="margin-top: 15px; margin-bottom: 15px; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;">संगठन मजबुत बनाएको खण्डमा केन्द्रीय चुनावमा पुग्दा नपुग्दै माओवादी पार्टी फेरि पहिलो पार्टी हुने उनको भनाइ थियो।</p><p style="margin-top: 15px; margin-bottom: 15px; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;"><strong>विप्लव, बाबुराम र वैद्यलाई जनताले अनुमोदन गरेनन्</strong></p><p style="margin-top: 15px; margin-bottom: 15px; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;">अध्यक्ष प्रचण्डले माओवादीबाट फुटेर गएका अन्य दलहरुलाई जनताले अनुमोदन नगरेको बताएका छन्। ‘हिजो के के न गर्छु भनेर जानुभएका साथीहरुलाई जनताले अनुमोदन गरेनन्’, उनले भने, ‘अब उहाँहरु यही पार्टीमा फर्कनुको विकल्प छैन।’</p><p style="margin-top: 15px; margin-bottom: 15px; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;">बाबुराम, विप्लव र वैद्यको पार्टीबाट ठूलो संख्यामा माओवादी केन्द्रमा प्रवेश गर्न लागेको भन्दै उनले भने, ‘वैद्य, बाबुराम र विप्लव जीहरु स्वयं नै यहि पार्टीमा आउनुपर्छ भनेर जोड गरिरहेको छु।’</p><p style="margin-top: 15px; margin-bottom: 15px; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;"><strong>&nbsp;हामीले हाम्रा काम, एजेन्डा र नीति जनतालाई बुझाउन सकेनौँ</strong></p><p style="margin-top: 15px; margin-bottom: 15px; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;">अध्यक्ष प्रचण्डले माओवादीले निर्वाचनमा अपेक्षित नतिजा हासिल गर्न नसक्नुमा प्रचार–प्रसारको अभाव रहेको बताए। ‘स्थानीय तहको निर्वाचनमा अरुले कुहेको पिठो पनि बेचे, हामीले चामलपनी बेच्न सकेनौं’, उनले भने, ‘आजकल जमाना मार्केटिङको छ, चामल बासमती नै भएपनि मार्केटिङ गर्न नसके केही नहुने रहेछ।’</p><p style="margin-top: 15px; margin-bottom: 15px; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;">उनले आफ्नो सरकारले गरेका राम्रा काम जनतालाई बुझाउन नसकेको बताए। उनले भने, ‘सरकारमा राम्रो काम गर्ने हामी, नीति र एजेन्डा हाम्रै तर नतिजा किन हाम्रो पक्षमा आएन?’ उनले यसबारेमा गम्भीर भएर सोच्न नेता, कार्यकर्तालाई निर्देशन समेत दिए।</p><p style="margin-top: 15px; margin-bottom: 15px; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;"><strong>माओवादी सच्चा राष्ट्रवादी</strong><br>उनले नक्कली राष्ट्रवादलाई जनतामा भ्रमित गर्ने गरि प्रचार गरिएको भन्दै त्यसलाई चिर्नुपर्ने बताए। उनले भने, ‘हामीले अरु केही गर्नु पर्दैन नक्कली राष्ट्रवादको भ्रम चिरे पुग्छ। जनतालाई हिमाल पहाड तराई जोड्ने माओवादीको सच्चा राष्ट्रवाद देखाए पुग्छ।’</p><p style="margin-top: 15px; margin-bottom: 15px; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;">अध्यक्ष प्रचण्डले चुनाव हार्नुको मुख्य कारण आन्तरिक रहेको बताएका छन्। जनतामा नजानु, भद्दा कमिटी हुनु, निर्णय प्रक्रिया बेठिक हुनु पनि हारका कारण भएको उनको भनाइ थियो।</p><p style="margin-top: 15px; margin-bottom: 15px; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;">कार्यक्रममा महाङ्काल गाउँपालिका नेपाली काङ्ग्रेसका महासमिति सदस्य गणेश केसी लगायत माओवादी केन्द्रमा प्रवेश गरेका थिए।</p>', '2017-07-19 15:08:50', '2017-07-19 15:08:50'),
(24, '‘कुटुमा कुटु’की मेलिना आफ्नो सफलतामा अहिलेकी हिट गायिका मेलिना राई ‘थ्याङ्क्स ड्याडी’ भन्दै बा सम्झिन्छिन्', 'entertainment', '2.PNG', 'melina-hit-female-singer', '2017-07-19', '<p style="margin-bottom: 15px; text-align: justify; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;">‘कम्मरमाथि पटुकी’देखि ‘शिरैमा शिरबन्दी’सम्म आइपुग्दा गायिका मेलिना राईले सफलताको स्वाद किस्ताकिस्तामा चाख्दै आएकी थिइन्। तर पछिल्लो गीत ‘कुटुमा कुटु’ले भने उनलाई सफलताको चुचुरोमै पुर्‍याइदिएको छ।</p><p style="margin-top: 15px; margin-bottom: 15px; text-align: justify; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;">करिब डेढ महिनाअघि युट्युब मार्फत सार्वजनिक भएको ‘२ रुपैयाँ’ फिल्मको यो गीत अहिले जताततै घन्किरहेको छ। युट्युबमा छोटो समयमै करोड भ्युज नाघ्न सफल भएपछि उनी अहिले धेरै खुसी छिन्।&nbsp;गीतमा राजनराज शिवाकोटीको शब्द–सङ्गीत छ भने मेलिनाबाहेक राजनराज र राजन ईशानले पनि स्वर दिएका छन्।</p><p style="margin-top: 15px; margin-bottom: 15px; text-align: justify; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;">एकैदिन पाँच ओटासम्म गीत गाउने अवसर पाएकी उनी औसतमा हप्ताको चार पाँच ओटा गीतको रेकर्डिङमा व्यस्त रहेको बताउँछिन्। कुटुमा कुटुकी गायिका भनेर चर्चा बढेपछि उसै व्यस्त उनको दौडधुप आगामी दिनमा व्यस्तता&nbsp;थप बढ्ने पक्का छ। तर भन्छिन्, ‘एकै दिन धेरै गीत रेकर्ड गर्दा गीतमा न्याय गर्न गाह्रो हुन्छ। अलिकति ग्याप राख्दै काम गर्ने प्रयास गर्दैछु।’</p><p style="margin-top: 15px; margin-bottom: 15px; text-align: justify; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;"></p><div class="cs_img " style="max-width: 100%; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px; width: 1000px;"><p style="margin-bottom: 15px;"><img src="https://nepalkhabar.com/get_img?ImageWidth=1000&amp;ImageHeight=1013&amp;ImageId=27353" border="0" style="max-width: 100%; height: auto;"></p></div><p style="margin-top: 15px; margin-bottom: 15px; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;"></p><p style="margin-top: 15px; margin-bottom: 15px; text-align: justify; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;">कर्स्याङमा&nbsp;जन्मेकी उनको बाल्यकालको एक चरण भारतको पश्चिम बंगालमै बित्यो। उनको १० वर्षदेखि यताको उमेर भने काठमाडौंमै बितिरहेको छ। उनका बुबा जिवेश राई सङ्गीतकार भएकोले उनले सानैदेखि घरभित्रै सङ्गीत सिक्ने अवसर पाइन्। गीत नगाउँदा पनि उत्तिकै सुरिलो लाग्ने आवाजमा बोलिन्, ‘घरकै माहोलले सानैमा गायन क्षेत्रमा स्थापित हुने सपना देखेँ। ड्याडीले मेरो सपना पुरा गर्ने आधार बनाइदिनुभयो।’</p><p style="margin-top: 15px; margin-bottom: 15px; text-align: justify; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;">शास्त्रीय सङ्गीतका गुरु बुबासँग सङ्गीत सिक्न भने मेलिनालाई हम्मेहम्मे पर्यो। बुबा साह्रै कडा मिजासका रहेछन्। तर उनी भने सानैदेखि चुलबुले स्वभावकी। बुबाको कडा मिजासबारे नरम स्वरमा सुनाइन्, ‘सानो गल्तीमा पनि ठूलो सजाय भोग्नुपथ्र्यो।’ मेलिना र उनकी बहिनी जुना बिहानै उठ्थे र आँखा मिच्दै रियाज गर्न बस्थे। कलिलै उमेरमा निद्रा पनि नभागेको समयमा कठोर अनुशासनको रियाजले उनीहरुलाई झनै निद्रा लगाउँथ्यो। तर दुबैले चुँसम्म गर्न पाउँदैनथे।</p><p style="margin-top: 15px; margin-bottom: 15px; text-align: justify; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;">गाली खाएका पुराना दिन सम्झिन्, ‘सबेरै उठेर रियाज गर्दा सानो गल्तीमा पनि ठूलो गाली सुन्दा मनै खिन्न हुन्थ्यो। बहिनी मभन्दा पाँच छ वर्ष सानी भएकोले पनि मलाई नै बढी गाल पथ्र्यो।’<br>तर कुनै बेलाको कडा अनुशासन र तनावपूर्ण रियाज आज उनका लागि वरदान सावित भएको छ। उनलाई त्यही कडा अनुशासनभित्र बाँधिएर गरेको मिहिनेत आज काम लागिरहेको महसुस हुन्छ। मेवाची घराना सङ्गीतको जगकै बलमा आज उनी लोक, आधुनिक, डान्स नम्बरदेखि रागमा आधारित गीतहरु सहजै गाउन सक्ने भएकी छन्।</p><p style="margin-top: 15px; margin-bottom: 15px; text-align: justify; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;">त्यही भएर हरेक सफलताको साक्षात्कारसँगै उनी मनैमन भनिरहेकी हुन्छिन, ‘थ्याङ्कस अ लट ड्याडी।’</p><p style="margin-top: 15px; margin-bottom: 15px; text-align: justify; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;"></p><div class="cs_img " style="max-width: 100%; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px; width: 1000px;"><p style="margin-bottom: 15px;"><img src="https://nepalkhabar.com/get_img?ImageWidth=1000&amp;ImageHeight=667&amp;ImageId=27355" border="0" style="max-width: 100%; height: auto;"></p></div><p style="margin-top: 15px; margin-bottom: 15px; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;"></p><p style="margin-top: 15px; margin-bottom: 15px; text-align: justify; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;">उनलार्ई सानोमा मनिला&nbsp;सोताङको आवाज सुन्दा लाग्थ्यो, ‘आहा! मैले पनि उहाँजस्तै मीठो स्वरमा गीत रेकर्ड गर्न पाए कति रमाइलो हुँदो हो।’ बुबाकै संगतले उनी सानैदेखि जगजित सिंह, लता मंगेशकर आदिका गीत गजल पनि गुनगुनाउने भइन्। उनका बुबा साधना सरगमका फ्यान रहेछन्।</p><p style="margin-top: 15px; margin-bottom: 15px; text-align: justify; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;">यही प्रभावमा उनी पनि थाहै नपाइ साधना सरगमको ठूलै फ्यान बनिन्।<br>अरुका गीत सुनेर गीत रेकर्ड गर्ने सपना बुनिरहेकी उनले आफ्नो इच्छा पूरा गर्न पनि धेरै समय कुर्नु परेन। आफ्नो बुबाकै शब्द सङ्गीतमा उनले ११ वर्षकै उमेरमा पहिलो स्वदेश गीत रेकर्ड गरिन्, ‘हिमालको काखमा यति सुन्दर देश’।<br>त्यसपछि केही गीतमा कोरसका रुपमा स्वर दिन थालिन्। उनले पढेको विद्यालय अरुण ज्योति विद्या मन्दिरका सङ्गीत गुरुले तयार पारेको गीतमा भने उनले एकल स्वर दिएकी थिइन्। गीतको बोल याद नभए पनि त्यसको मर्म याद रहेछ, ‘त्यो माइती र चेलीको सम्बन्धमा आधारित गीत थियो।’</p><p style="margin-top: 15px; margin-bottom: 15px; text-align: justify; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;">यस्तै अनुभव बटुल्दै अघि बढेकी उनले २०७१ सालमा आधुनिक गीति सङ्ग्रह ‘बहार’ सार्वजनिक गरेयता पछाडि फर्केर हेर्नुपरेको छैन। उनको सुरिलो आवाज र गायकीलाई सङ्गीत क्षेत्रले सुरुमै पत्यायो।</p><p style="margin-top: 15px; margin-bottom: 15px; text-align: justify; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;">तर वर्तमान अवस्थामा अन्य धेरै सङ्गीतकर्मीका लागि झैं उनका लागि पनि फिल्म ‘टर्निंग पोइन्ट’ बन्यो। उनले नोभेम्बर रेनका लागि गाएको ‘तिमीलाई म के भनूँ’ले नै चर्चा पायो। तर उनको नामलाई नै चिनाउने गीत भने ‘नाइ नभन्नु ल ३’को ‘कम्मरमाथि पटुकी’ हो। मेलिनालाई पनि यस्तै लाग्छ, ‘गीत त यसअघि पनि हिट थिए।</p><p style="margin-top: 15px; margin-bottom: 15px; text-align: justify; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;">तर मेलिना भनेर सबैतिर चिनाएको यही गीतले हो।’</p><p style="margin-top: 15px; margin-bottom: 15px; text-align: justify; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;"></p><div class="cs_img " style="max-width: 100%; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px; width: 1000px;"><p style="margin-bottom: 15px;"><img src="https://nepalkhabar.com/get_img?ImageWidth=1000&amp;ImageHeight=1326&amp;ImageId=27354" border="0" style="max-width: 100%; height: auto;"></p></div><p style="margin-top: 15px; margin-bottom: 15px; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;"></p><p style="margin-top: 15px; margin-bottom: 15px; text-align: justify; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;">त्यसपछि ‘हाउ फन्नी’ फिल्मको ‘शिरैमा शिरबन्दी’ बोलको गीत अघिल्लो भन्दा हिट भयो। यो पनि उनको करोड भ्युज नाघेको गीत हो। तर, अहिले ‘कुटुमा कुटु’ले जुन रफ्तार लिइरहेको छ, त्यो उनकै लागि पनि अकल्पनीय थियो। गीत चर्चित भएपछि मेलिनालाई धेरैले सेलिब्रेटीको नजरले हेर्ने नै भए। तर उनी आफैंलाई भने आफ्नो पुरानो सादगी नै प्यारो लाग्दोरैछ। अलिकति अनकनाउँदै प्रस्ट्याउन खोजिन्, ‘हाहा, होइन हउ। ममा सेटिब्रेटी वा स्टार भन्ने फिलिङ्ग्स नै आउँदैन। म सामान्य मान्छे हुँ।’</p><p style="margin-top: 15px; margin-bottom: 15px; text-align: justify; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;">सायद यही सरलपनाले होला, उनी आफ्ना फ्यानसँग जम्काभेट हुँदा भित्रभित्रै लजाउँछिन् रे। सपिङ मल वा अन्य सार्वजनिक थलोमा उनीसँग बोल्न वा फोटो खिचाउन आहने जमातको सामना गर्न उनलाई सहज लाग्दैन रे। तर स्रोताको प्रतिक्रियालाई सहज रुपमा लिनु पर्ने पनि उनलाई थाहा छ। त्यही भएर उनले जसोतसो एडजस्ट गर्न सिकिसकेकी छन्।</p><p style="margin-top: 15px; margin-bottom: 15px; text-align: justify; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;">मेलिनाले आधुनिक शैलीका गीतको तुलनामा फिल्ममा भएका डान्स नम्बरबाट अत्याधिक चर्चा बटुलेकी हुन्। आफैं पनि नाच्न रमाउने उनी चर्चा भयो भन्दैमा मात्तिन नहुने धारणा राख्छिन्।&nbsp;<br>अपवादबाहेक आधुनिक सङ्गीत क्षेत्रमा सक्रिय कलाकारले अहिले आफैं लगानी गरेरै पनि फिर्तामा आर्थिक हिसाबले घाटा बेहोर्नु पर्ने स्थिति छ।</p><p style="margin-top: 15px; margin-bottom: 15px; text-align: justify; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;">यस्तोबेला सङ्गीतकर्मीलाई फिल्मी सङ्गीतले ठूलो टेवा दिएको छ। उनको विश्लेषण छ, ‘फिल्ममा ठूलो लगानी हुन्छ। यसबाट सङ्गीतकर्मीलाई पनि भरथेग भइरहेको छ। तर फिल्मका गीतले मात्रै सन्तुष्ट हुने अवस्था भने छैन।’</p><p style="margin-top: 15px; margin-bottom: 15px; text-align: justify; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;">त्यही भएर उनी ‘बहार’पछि अर्को एकल सङ्ग्रह ल्याउने तयारी पनि गरिरहेकी छन्। सुगम सङ्गीतमा आफूले चाहेअनुसारको काम गर्न पाइने र आफ्नो क्षमतालाई प्रमाणित गर्न पनि पाइने भएकोले नै उनी फिल्मी गीतमा मात्रै खुम्चिन चाहन्निन्। उनको बुझाइ छ, ‘अहिलेका फिल्मका गीत अलि बढी व्यवसायिक छन्।’</p><p style="margin-top: 15px; margin-bottom: 15px; text-align: justify; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;">व्यवसायिकताले नयाँ नयाँ कलाकारलाई अवसर सिर्जना गर्ने भएकोले व्यवसायिकतालाई नराम्रो भने मान्दिनन्। परिपक्व भावमा पोखिन्, ‘केही गीत तनमा बस्छन्, केही मनमा। म स्रोताहरुको मनमा बस्ने गीत गाउन चाहन्छु।’</p><p style="margin-top: 15px; margin-bottom: 15px; text-align: justify; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;"></p><div class="cs_img " style="max-width: 100%; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px; width: 1000px;"><p style="margin-bottom: 15px;"><img src="https://nepalkhabar.com/get_img?ImageWidth=1000&amp;ImageHeight=712&amp;ImageId=27352" border="0" style="max-width: 100%; height: auto;"></p></div><p style="margin-top: 15px; margin-bottom: 15px; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;"></p><p style="margin-top: 15px; margin-bottom: 15px; text-align: justify; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;">सङ्गीत मेलिनाको परिचय मात्रै होइन, आत्मसन्तुष्टि पनि हो। तर यसमा अलमलिँदा अलमलिँदै उनले औपचारिक शिक्षालाई भने परपर धकेल्नु पर्यो। समाजशास्त्र र अंग्रेजीमा स्नातक उनी अब चाँडै स्नातकोत्तरको पढाइ अगाडि बढाउन चाहिन्छन्। उनी घरमै बुबाबाट सङ्गीत सिकेकोले उनलाई सङ्गीत क्षेत्रमा प्रवेश गर्न सहज भयो। तर यस क्षेत्रमा स्थापित हुन आफूले प्रशस्तै मिहिनेत पनि गर्नुपरेको उनी बताउँछिन्।&nbsp;</p><p style="margin-top: 15px; margin-bottom: 15px; text-align: justify; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;">आजको तीव्र प्रतिस्पर्धी समयमा नसिकी नबुझी, कसैको आशीवार्दले मात्रै सङ्गीत क्षेत्रमा टिक्न नसकिने उनको मूल्यांकन छ। भन्छिन्, ‘आफूलाई स्थापित गर्न त मिहिनेत आफैंले गर्ने हो।’ उनले गरेको मिहिनेत र उनमा भएको लगनले नै हो, आज धेरैभन्दा धेरै स्रोताको जिब्रोमा उनका गीत झुन्डिन थालेका छन्।</p><p style="margin-top: 15px; margin-bottom: 15px; text-align: justify; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;">तर रमाइलो कुरा, ‘कुटुमा कुटु’ गीत यति चाँडै चर्चित भयो कि, उनलाई लागिरहेको छ, ‘कुटुमा कुटुकी गायिका मै हुँ भन्ने मेरै छिमेकी र आफन्तले समेत थाहा पाउन बाँकी छ।’</p>', '2017-07-19 15:13:22', '2017-07-19 15:13:22'),
(25, 'पर्यटकीय नगरी पोखरामा फोहोर धन्दाः विधि मिचेर निजी संस्थालाई ठेक्का', 'community', '3.PNG', 'touristic-place-being-polluted', '2017-07-19', '<p style="margin-bottom: 15px; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;">पोखराको फोहोर व्यवस्थापन गर्ने जिम्मा निजी क्षेत्रलाई दिँदा ठूलै अनियमितता भएको आशंका जन्मिएको छ। नगरबासीबाट सरसफाई शुल्कवापत मोटो रकम उठाउन मिल्ने मुख्य सहरी क्षेत्रको फोहोर व्यवस्थापन गर्ने जिम्मा निजी क्षेत्रलाई दिइएको छ भने कम रकम उठ्ने र टाढाको फोहोर नगरपालिकाले आफैं उठाउँछ।</p><p style="margin-top: 15px; margin-bottom: 15px; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;">फोहोर उठाउने कार्यलाई व्यवस्थित गर्ने नाममा तत्कालीन पोखरा उपमहानगरपालिकाले केही निजी संस्थालाई ठेक्का दिएको थियो। ती संस्थालाई फोहोर उठाउने ठेक्का दिँदा प्रक्रिया नपु¥याई दिइएको पाइएको हो। उपमहानगरबाट पोखरा महानगर बनेको भएपनि फोहोर उठाउने प्रक्रिया पुरानै कायम छ। सुरुमा कुनै टेन्डर तथा सूचना प्रकाशित नगरी निजी संस्थालाई फोहोरमैला व्यवस्थापनको जिम्मा दिइएको र पछि चोर बाटोबाट सम्झौताको म्याद थपिदै आएको छ।</p><p style="margin-top: 15px; margin-bottom: 15px; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;">स्थानीय स्वायत्त शासन ऐन २०५८ र नियमावली २०६४, फोहोरमैला व्यवस्थापन ऐन २०६८ ले तोकेको प्रक्रियाको बर्खिलाप पोखराले निजी संस्थालाई फोहोरमैला व्यवस्थापनको जिम्मा दिएको हो। निजी संस्था वा सामुदायिक संस्थाबाट फोहोरमैला व्यवस्थापन गराउँदा स्थानीय निकायले राष्ट्रिय स्तरको ‘क’ वर्गको पत्रिकामा सूचना प्रकाशित गरी बोलपत्र आह्वान गरेर प्रतिस्पर्धा गराई फोहोरमैला व्यवस्थापनको जिम्मा दिनुपर्छ। त्यतिमात्रै होइन, फोहोरमैला उठाउने संस्थाले जनचेतना अभिवृद्धि गर्ने, सौन्दर्यीकरणका कार्यक्रमसमेत गर्नुपर्ने नियममा उल्लेख छ। सम्झौतामुताबिक काम नगर्न महानगरपालिकाले नै सघाइरहेको छ।</p><p style="margin-top: 15px; margin-bottom: 15px; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;">सार्वजनिक निजी साँझेदारीको आधारमा पोखरामा अहिले फोहोर व्यवस्थापन गर्ने ५ संस्था छन्। पोखरा फोहोरमैला वातावरणीय नेपालले वडा नम्बर ३, ४, ८ र ९, नेपाल पब्लिक हेल्थ एन्ड इन्भार्मेन्ट डेभलपमेन्ड प्रालिले १, २ र ५, ग्रिन मार्ट प्रालिले ६, ११, १२ र १३ को जिम्मा पाएका छन्। वेस्ट म्याजम्यान्ट एन्ड रिसाइकल प्रालिले १० र १५ नम्बर वडाको फोहोर व्यवस्थापनको जिम्मा पाएको छ। यी संस्थालाई बिनाप्रतिस्पर्धा फोहोर व्यवस्थापनको जिम्मा दिइएको हो।</p><p style="margin-top: 15px; margin-bottom: 15px; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;">सार्वजनिक साझेदारी अन्तर्गत २०६५ सालमा पोखरा फोहोरमैला व्यवस्थापन प्रालिलाई सुटुक्क नगरपालिका छिराइएको थियो। अन्तिम पटक २०७१ मा ३ वर्षका लागि म्याद थपेर फेरि फोहोर व्यवस्थापनको ठेक्का दिइएको यो संस्थाको म्याद असार मसान्तमा सकिँदैछ। २०७३ वैशाख १७ गते ग्रिनमार्ट प्रालिलाई प्रक्रिया मिचेरै ३ वर्षका लागि ठेक्का दिइएको थियो भने वेस्ट म्यानेजम्यान्ट प्रालि र पब्लिक हेल्थसँग २०७३ पुस १४ गते एकै दिन नियममिपरीत फोहोर व्यवस्थापनको सम्झौता गरिएको थियो।</p><p style="margin-top: 15px; margin-bottom: 15px; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;">किन प्रक्रियाविपरीत फोहोर व्यवस्थापन सम्झौता गरियो त, भन्ने प्रश्नको उत्तर दिन महानगरका जिम्मेवार कर्मचारी हिच्किचाए। त्यो फोहोरमैला तथा वातावरण शाखा सम्हालेका चूडामणि तिवारी अहिले जिन्सी शाखामा छन्। त्यो जिम्मेवारी अहिले वरिष्ठ इन्जिनियर सुरेन्द्र पाण्डेलाई दिइएको छ । ‘मैले यो शाखा सम्हालेको २÷४ महिनामात्रै भयो।</p><p style="margin-top: 15px; margin-bottom: 15px; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;">त्यसैले यसको प्रक्रिया र सबै कुरा मलाई थाहा छैन,’ वातावरण महाशाखा प्रमुख पाण्डेले भने, ‘कर्मचारी हेरफेर हुँदा कसले के हेर्ने भन्ने कुरा नै अन्योल छ।’ तत्कालीन फोहोरमैला वातावरण शाखा हेरेका चूडामणि तिवारी यो कुरा हाकिमलाई नै सोध्नुपर्ने भन्दै पन्छिए। ‘मै एक्लैले फोहोर व्यवस्थापनको जिम्मा तपाईलाई भनेर दिने कुरा होइन। त्यसको नेतृत्व हाकिम साबकै हुन्छ,’ तिवारीले भने, ‘पुक्रिया नपु¥याई दिइएको चाहिँ हो। जो आयो उसैलाई प्रस्ताव हेरेर दिइयो।’</p><p style="margin-top: 15px; margin-bottom: 15px; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;">महानगरको मनपरीतन्त्रले पोखराको ठूलै आम्दानी गुमेको छ। सुरुमा फोहोर शुल्कवापत संकलन हुने रकमबाट ४० प्रतिशत पोखरा नगरले पाउने र ६० प्रतिशत रकम निजी संस्थाले पाउने गरी सम्झौता भएको थियो। अहिले व्यवस्थापनको जिम्मा पाएको संस्थाले नै ८० प्रतिशत लैजान्छ भने २० प्रतिशतमात्र नगरलाई आउँछ। यो प्रतिशत घटबढमा पनि लेनदेनमै भएको स्रोतले बतायो। यतिले मात्र नपुगेर कतिपय निजी संस्थालाई नगरपालिकाले गाडी र कर्मचारीसमेत उपलब्ध गराइएको छ।</p><p style="margin-top: 15px; margin-bottom: 15px; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;">व्यवस्थापनको जिम्मा पाएको संस्थाले कसरी फोहोर उठाइरहेको छ, कुन कुन दिन कुन कुन वडामा जान्छ भन्ने जानकारी न नगरपालिकालाई छ, न वडावासीलाई। महानगरसँग कति सेवाग्राहीमार्फत् फोहोरमैलाको कर दाखिला भयो? कति सेवाग्राही करको दायरामा समेटिन बाँकी छन् भन्ने सामान्य अभिलेखसमेत छैन।</p><p style="margin-top: 15px; margin-bottom: 15px; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;">नगरपालिकाले नै फोहोरमैला १९ समूहमा विभाजन गरी शुल्क निर्धारण गरेको छ। मासिक ८ रुपैयाँदेखि ७ हजारसम्म शुल्क तय गरिएको छ। तर, सामान्यदेखि अधिकतम शुल्क तिर्ने कति छन् भन्ने अभिलेख महानगरमा छैन। महानगरका सम्बन्धित कर्मचारी भन्छन्, ‘अधिकतम सेवामार्फत अधिकतम कर संकलन गरिन्छ। तथ्यांक निजी संस्थाबाट मगाउनुपर्छ, अहिले छैन।’</p><p style="margin-top: 15px; margin-bottom: 15px; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;">निजी कम्पनी नाफामुखी हुन्छ । उसको निगरानी र अभिलेख राख्ने नगरपालिकाले नै हो। नगरपालिकाका कर्मचारी नगरपालिकाको तलव, भत्ता लगायतका सुविधा लिएर ती कम्पनीको कर्मचारी बन्नु, नगरपालिकाले लाखौं खर्चेर ल्याएका फोहर बोक्ने गाडी निजी क्षेत्रको व्यवसाय चलाउने साधन बन्नु लगायतका बेथितिले फोहोरमैला व्यवस्थापनको पाटो मागी खाने भाँडो बनेको प्रष्ट हुन्छ।</p><p style="margin-top: 15px; margin-bottom: 15px; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;">फोहोरमैला व्यवस्थापनको यही विषयलाई हालै सकिएको महानगरको प्रथम नगर सभाका सभासदले यो विषयलाई जोडतोडका साथ उठाए। ‘फोहोर व्यवस्थापन मनपरी भइरहेको विषयमा हामीले आपत्ति जनाउँदै सुटुक्क दिइएको सम्झौता खारेज गर्न आवाज उठाएका छौं,’ वडा नम्बर ९का अध्यक्ष रामराज लामिछानेले भने, ‘फोहोरमैला व्यवस्थापन महानगरले टेन्डर खुलाएर दिनुपर्छ र वडाले आफैं व्यवस्थापन गर्न सक्छ भने वडालाई जिम्मा दिनुपर्छ भनेका छौं । यो निर्णय गर्ने जिम्मा कार्यकालिकालाई लगाइएको छ।’</p><p style="margin-top: 15px; margin-bottom: 15px; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;">प्रक्रिया पूरा नगरी महानगरले पैसाको खेलमा निजी संस्थासँग सम्झौता गर्ने गरेको आरोप नगरपालिकाकै कर्मचारी तथा फोहोरमैला क्षेत्रमै काम गर्नेहरू बताउँछन्। निजी संस्थामै काम गरेका पूर्व कर्मचारी मन्त्री उलकले घूस ख्वाएरै काम गर्ने गरेको हाकाहाकी स्वीकारे। ‘प्रत्येक सम्झौता गर्दा र नवीकरण गर्दा नगरपालिकाका कर्मचारीलाई २÷४ लाख बुझाउनुपर्छ,’ पोखरा महानगरपालिकामा भेटिएका उलकले भने, ‘यहाँ फोहोरलाई धन्दा बनाएर चलाउने काम भइरहेको छ।’</p><p style="margin-top: 15px; margin-bottom: 15px; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;">यहाँ महानगरका १३ वटा र निजी संस्थाका ११ वटा गरी २४ वटा फोहोरमैला उठाउने गाडी छन् भने १ सय १० भन्दा बढी कर्मचारी छन्। फोहोर व्यवस्थापनको जिम्मा पाएका संस्था र नगरपालिकाले यो आर्थिक वर्षमा १ करोड ५५ लाख कर संकलन गरेको छ। पब्लिक हेल्थ र वेस्ट म्यानेजमेन्टले भने अहिलेसम्म कर दाखिला गरेका छैनन्। कर निर्धारण र शुल्क असुली वैज्ञानिक बनाउनसमेत महानगरको चासो छैन।</p>', '2017-07-19 15:18:02', '2017-07-19 15:18:02'),
(26, 'डलर खेती नराम्रो काम हो?-२', 'economy', '4.PNG', 'is-dollar-business-a-crime', '2017-07-19', '<p style="margin-top: 15px; margin-bottom: 15px; text-align: justify; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;"><strong>भाषिक सामन्तवादको भुलभुलैया</strong><br>हरेक सुविधा,&nbsp;प्रक्रिया र तहसम्म आम जनताको पहुँच हुनुपर्छ भन्ने गैससहरूको प्यारो आदर्शवाक्य हो। तर गैसस सञ्जालभित्र छिर्न र अघि बढ्न अंग्रेजी अनिवार्य छ। शिक्षा र चेतनाको दृष्टिले सीमान्त तहमा रहेका वास्तविक लक्षित समुदायको त कुरै छाडौं, साक्षर, औसत शिक्षित र अपेक्षाकृत जान्ने–बुझ्नेका लागि पनि अंग्रेजी यस्तो तर्साउने ख्याक बनेको छ, जसले त्यो पहुँचमा अलंघ्य पर्खाल खडा गर्छ।&nbsp;</p><p style="margin-top: 15px; margin-bottom: 15px; text-align: justify; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;">समुदायलाई के चाहिएको छ भन्ने प्रपोजल पनि अंग्रेजीमा लेख्नुपर्ने र भएको कामको रिपोर्ट पनि अंग्रेजीमै पेश गर्नुपर्ने बाध्यताले आम नेपाली समुदाय र गैससबीच आपसी तादात्म्य, हार्दिकता र आत्मीयता उत्पन्न हुनै नदिने खाडल खनेको छ। अंग्रेजी नजानेकै मात्र कारणले समुदायका सदस्य र कार्यकर्तामा असहाय मनस्थिति एवं हीनभावना गाढा बन्दै गएको तीतो सत्य अब लुकाएर लुक्न सक्ने अवस्था छैन। यसैले ‘आम जनताको पहुँच’ का सन्दर्भमा गैससहरूको यो आदर्श हात्तीको देखाउने दाँतझैं एउटा ढोंगी नाराबाहेक केही होइन भन्ने धारणा तिनै आम जनताले बनाएका छन्।</p><p style="margin-top: 15px; margin-bottom: 15px; text-align: justify; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;">शिक्षित समुदायबीच हुने गोष्ठी–सेमिनारमै पनि छलफल र विमतिको सम्भावना न्यून गर्नुछ भने अंग्रेजी माध्यममा कार्यक्रम चलाउने रणनीतिक चलाखी अपनाइन्छ। त्यसबेला अत्यधिकांश सहभागीहरू आधाउधि मात्र कुरो ठम्याएर पूरै बुझेजस्तो अभिनय गर्न र हाँसोको पात्र होइने डरले झ्वाट्ट प्रश्न सोध्नबाट बच्न विवश हुन्छन् भने अंग्रेजीको तरबार टाउकामाथि झुण्ड्याइदिएपछि आम नेपालीको हालत के होला, सहजै अनुमान गर्न सकिन्छ। कार्यक्षम र लगनशील भन्दा पनि अंग्रेजी भट्याउन र अंग्रेजीमा प्रपोजल–रिपोर्ट तयार गर्न सक्ने मानिस नै गैससको पहिलो प्राथमिकतामा पर्छ जसले एउटा अर्को आडम्बरी दुनियाँ खडा गरेको छ।</p><p style="margin-top: 15px; margin-bottom: 15px; text-align: justify; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;">यो भाषिक सामन्तवादले जनतालाई सधैं भुलभुलैयामा राख्ने, बौद्धिक रूपमा परमुखापेक्षी बनाउने, मानसिक रूपमा अंग्रेजीबाजको कृपामा निर्भर तुल्याउने र समग्रमा हीन मनोवृत्ति सृजना गर्ने मात्र उपलब्धि हासिल गरेको छ। यहींनेर, आम जनताका मस्तिष्कमा जब्बर प्रश्नहरूले शिर उठाउँछन्- गैससहरू कसका लागि काम गर्छन्, नेपालीका लागि कि विदेशीका लागि? नेपालीका लागि हो भने यो भाषिक कुचक्र किन? के समुदाय यसैगरी बलियो हुन्छ? यही बाटोबाट उसमा आत्मसम्मान र आत्मविश्वास जगाउन सकिन्छ? पारदर्शिता र पहुँच बढाउने आधार यही हो? जति सक्षम र समर्पित समाजसेवी भए पनि अंग्रेजी नजान्नासाथ, पुरानो नेपाली उखानको ‘ज्यालामा नमिलेको डुम’ हुनुपर्ने यो कस्तो जातपातविरोधी, समतामुखी, सामाजिक अवधारणा हो? गैससका आडमा के गर्न खोजिएको हो? यो माखेसाङ्लो तोड्न नेपाली गैससहरू किन पहल गर्दैनन्?</p><p style="margin-top: 15px; margin-bottom: 15px; text-align: justify; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;"><strong>दिशाहीन र लाचार</strong>&nbsp;<strong>सरकार</strong><br>गैससहरूको नियमन, अनुशासन, समन्वय र अनुगमनको जिम्मेदारी सरकारको हो। त्यसनिम्ति नियम–कानूून र औपचारिक निकायहरूको व्यवस्था नभएको होइन। तर आफ्नै कमजोरी र अनुशासनहीनताका कारण गैससबारे सरकार दिशाहीन, अन्योलग्रस्त र असहाय देखिँदै आएको छ। नियम–कानून अनुसार चल्न चाहनेलाई ऊ अनावश्यक झमेलामा फँसाएर हतोत्साहित पार्छ अनि नियमकानून छल्ने र नटेर्नेलाई केही गर्न नसकेर लाचार बन्छ।</p><p style="margin-top: 15px; margin-bottom: 15px; text-align: justify; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;">एउटा उदाहरण हेरौं, गैससले विदेशी आर्थिक सहयोग लिनुपर्दा समाज कल्याण परिषद्को स्वीकृति लिनुपर्ने कानून छ तर अधिकांशले त्यसलाई टेरपुच्छर लाएका छैनन्। कानून बमोजिम कार्बाही चलाउन नसकेपछि एकपल्ट एउटा सूचना जारी गरियो– कम्तीमा आजसम्मको अडिट रिपोर्ट त पेश गर भनेर। तर करोडौंको बजेट खेलाउने गैससहरू रिपोर्ट देखाउनुको सट्टा क्रुद्ध भएर विरोधमा उत्रे। जिम्मेदार निकाय लोप्रेकान लगाएर चूूप लाग्यो। जो रिपोर्ट लिएर हाजिर भए, ती ससाना गैससहरूलाई प्रोत्साहन दिनुको सट्टा अनेक चक्करमा फँसाएर थकित पारियो। देशभित्रकै गैससलाई त कानूनी घेराभित्र हिँडाउन नसक्ने सरकारले अन्तर्राष्ट्रिय दाता गैससलाई अँचेट्न सक्ने कुरै भएन। त्यसैले गुपचुप पैसा प्रवाहित गर्ने र हिसाबकिताब लुकाउने तिनको धन्दा ढुक्कै जारी छ।</p><p style="margin-top: 15px; margin-bottom: 15px; text-align: justify; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;">विश्व स्वास्थ्य संगठन र युएसएड जस्ताबाट आएका परियोजनाको समेत हिसाबकिताब लुकाइएको पोल महालेखा परीक्षकको प्रतिवेदनले खोलेको छ भने उच्च तहका सरकारी हाकिमहरूले परियोजनाको रकम व्यक्तिगत खातामा पुर्‍याउने गरेको र संझौतादेखि समाप्तिसम्मको गोलमालमा दाताहरूको समेत मिलोमतो रहने गरेको गुनासो अख्तियार दुरूपयोग अनुसन्धान आयोगले गरिरहेको छ। बहालवाला उच्च सरकारी अधिकारीहरू नै ठूलठूला गैससका अंशियार भएपछि यस्तो नहोस् पनि कसरी? सरकार उच्च नैतिक धरातलयुक्त, दरो र आत्मविश्वासपूर्ण भएको खण्डमा मात्र यसको उपचार सम्भव हुन्छ तर दुर्भाग्यवश लामो समयदेखि त्यस्तो सरकार देखा पर्न सकेको छैन। फलतः गैससहरूका सन्दर्भमा आफैंप्रतिको निरीहता र लाचारी नै सरकारको दृष्टिकोण बन्न पुगेको छ।</p><p style="margin-top: 15px; margin-bottom: 15px; text-align: justify; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;"><strong>राजनीतिका माध्यम</strong><br>सरकारको नेतृत्व गर्ने राजनीतिक पार्टी (यदाकदा पार्टीविहीन समेत)ले हो। राजनीतिक खेलाडीहरूका दृष्टिमा गैससहरू पनि राजनीतिकै माध्यम हुन्। जो गैसस कुनै पार्टी वा पक्षलाई सघाउ पुर्‍याउने उद्देश्यले काम गर्दैनन् तीबारे खासै ध्यान दिनु उनीहरू जरूरी ठान्दैनन्। तर आफूलाई पैसादेखि जनशक्ति र संगठन विस्तारसम्मका काममा सघाउने तथा आफ्ना कार्यकर्ताद्वारा सञ्चालित गैससहरूको संरक्षण गर्न, जति बदमासी गरे पनि प्रतिरक्षा गर्न र तिनका आडमा आफ्नो राजनीतिक एवम् अन्य अभीष्ट पूरा गर्न, सत्तामा रहँदा होस् वा विपक्षमा, उनीहरू एकदमै संवेदनशील र सक्रिय देखिन्छन्। तिनका दृष्टिमा ती गैसस आफ्नै अंगजस्ता, भ्रातृसंस्था जस्ता हुन्। भनिरहनु नपर्ला, इतर पार्टी वा पक्षसँग सम्बद्ध त्यस्तै गैससहरूप्रति चाहिं हरेक पार्टीको दृष्टिकोण सधैं नकारात्मक हुन्छ। सकेसम्म तिनको खेदो खन्नु उनीहरूले आफ्नो धर्म मानेका हुन्छन्। दाताहरूसँग आफ्नै क्षमताका आधारमा राम्रो सम्बन्ध विकास गरिसकेका र तिकडमले मात्र तल खसाल्न नसकिने इतर पक्षसम्बद्ध बलिया गैससहरू प्रत्येक पार्टी एवं पक्षका लागि टाउकोदुखाइको विषय बनेका छन्। मौका पर्नासाथ तीबारे विषाक्त टिप्पणी गर्न नेताहरू पछि पर्दैनन्।</p><p style="margin-top: 15px; margin-bottom: 15px; text-align: justify; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;"><strong>अपरिपक्व सञ्चारमाध्यम</strong><br>जहाँसम्म सञ्चारमाध्यमको कुरो छ, गैससका बारेमा उसले सम्यक् दृष्टिकोण विकसित गर्न सकेकै छैन। सोझो भाषामा भन्दा उसको दृष्टिकोण अत्यन्त सतही, अस्थिर र अपरिपक्व छ। जसले गोष्ठी–सेमिनार र होहल्ला गर्छ, तडकभडक देखाउँछ, घुमाउन लैजान्छ, प्रेस वक्तव्य दिन्छ, पत्रकार सम्मेलन गरिरहन्छ, लेख–रचना उपलब्ध गराउँछ उसकै मात्र आवाज अझ ठूलो स्वरमा बारम्बार प्रतिध्वनित गर्ने काम नै सञ्चारमाध्यमबाट भइरहेको छ। तिनको अन्तर्निहित उद्देश्य, प्रच्छन्न स्वार्थ, योग्यता–अयोग्यता र खराब नियतका कारण लुकेका–लुकाइएका पक्षलाई आफ्नै प्रयासले अगाडि ल्याउन र गैससका आडमा चलिरहेका गलत धन्दा, माफिया शैलीका क्रियाकलाप एवं भ्रष्टाचारका अन्तरकथा अनावरण गर्न, आकस्मिक संयोग बाहेक, डेगिलो प्रयास भएकै छैन।</p><p style="margin-top: 15px; margin-bottom: 15px; text-align: justify; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;">पजेरो चढ्ने राजनीतिक नेताहरूका विरुद्ध अनवरत खनिइरहने पत्रकारिता जगत्लाई निस्वार्थ समाजसेवा गर्छु भन्ने सामान्य आर्थिक हैसियतको व्यक्तिले पाँच–सात वर्षभित्रै पजेरो, महल र अथाह सम्पत्ति जोडेको रहस्य खोतल्न, अपवाद बाहेक, फिटिक्कै जाँगर नचल्नु उदेककै कुरो मान्नुपर्छ। त्यसको विपरीत, ठूलठूला गैससको नियमित तलब खाएर समाचार तथा विश्लेषणको बर्को ओढाउँदै तिनको नियमित विज्ञापन गर्ने र तिनले बल्छीका रूपमा राखेको पुरस्कारको चारो टिप्ने ध्येयले प्रचारमुखी लेखहरूको उत्पादन गर्ने प्रवृत्तिचाहिँ बाक्लै पाइन्छ। जसले स्वच्छ र निस्वार्थ भावले होहल्ला नगरी काम गरेको छ उसको आवाज बन्ने अथवा जहाँ गैसस पुग्नु जरूरी छ त्यतातिर बाटो देखाउने तहमै सञ्चारमाध्यम उक्लन सकेको छैन भने गैससको सर्वाङ्गीण चित्र तथा गन्तव्य यसमा सम्यक् प्रतिबिम्बित हुने युग कहिले आउला, अहिल्यै भन्न सकिंदैन।</p><p style="margin-top: 15px; margin-bottom: 15px; text-align: justify; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;">यतिबेला भन्न सकिने के मात्र हो भने नेपालको सञ्चारमाध्यम आफ्नो भूमिका बिर्सेर गैससहरूले डोर्‍याएकै बाटोमा हिंडिरहेको छ– प्रायः तिनकै एक्टिभिस्ट जस्तो बनेर त कहिले झ्वाट्ट दुश्मन जस्तो बनेर। मानौं ऊ घना जंगलमा छिरेर हराएको त्यस्तो मानिस हो, जो कुनै आवाज कानमा पर्नासाथ अत्तालिएर दगुर्छ, कहिले आवाजतिर त कहिले विपरीत दिशातिर।</p><p style="margin-top: 15px; margin-bottom: 15px; text-align: justify; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;"><strong>सर्वसम्मति</strong><br>प्रश्न उठ्छ, अरूले त धेरै नकारात्मक कुरा गर्छन् तर गैससहरूबारे स्वयं गैससहरूको दृष्टिकोण चाहिँ कस्तो छ? यहाँनेर पञ्चायतकालको सम्झना आउँछ। मण्डलेका रूपमा ‘व्यक्तित्व’ बनाएका र त्यसैका आधारमा आफ्नो हैसियत कायम गरेका विभिन्न व्यक्ति त्यसबेला मन्त्री हुन्थे। तर पत्रकारहरूसँग कुरा गर्दा उनीहरू देशलाई मण्डलेले खत्तम पारेको बताउँथे र आफूबाहेक अरू सबैको नाम लिएर तिनै मण्डलेले देश सिध्याए भन्दै सत्तोसराप गर्थे। गैससबारे गैससको मूल्यांकन पनि यस्तै हुने गरेको छ। हरेक गैसस आफू र आफ्नो दाता अथवा मित्र गैससले इमान्दारीसाथ काम गरेको बताउँछ र अरू साराले डलर खेती गरेको निष्कर्ष निकाल्छ। नागरिकहरूले जे जस्ता आरोप लगाउँछन्, आफूबाहेक अरू सबैका सन्दर्भमा ती शतप्रतिशत सही हुन् भन्ने उसको अभिमत हुन्छ र आफ्नो सही मूल्यांकन कसैले नगरेको गुनासो हुन्छ। गैससहरूको चरित्र र नियतबारे गैससहरूबीचमै यस्तो बृहत्तर सहमति कायम भएपछि अरूले आक्षेप लगाए भनेर के चित्त दुखाइराख्नु?</p><p style="margin-top: 15px; margin-bottom: 15px; text-align: justify; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;"><strong>यक्षप्रश्न</strong><br>हाम्रो परम्परागत मान्यता अनुसार समाजसेवा भनेको आफूलाई स्वेच्छाले समाजका निम्ति वितरित गर्ने जीवनशैली हो। विना करकाप, विना कुनै आशा। पैसाको मात्र होइन, यश र इज्जतको आशाभन्दा पनि माथि उठेपछि मात्र समाजसेवाको पवित्र संसारमा प्रवेश हुन सक्छ। आत्मसन्तुष्टि यसको एकमात्र प्राप्ति र उपलब्धि हो। ‘मलाई राज्यको कामना छैन, स्वर्गको आकांक्षा छैन, अरू त अरू मुक्तिको समेत अभिलाषा छैन, पीडित मानिसहरूको पीडा नाश गर्न पाऊँ, यही एकमात्र चाहना छ,’ राजा रन्तिदेवको यो उद्गार समाजसेवाको आदर्शवाक्य मानिन्छ। एउटा हातले दिएको अर्को हातले थाहा नपाओस् भन्ने हाम्रो आदर्शले समाजसेवा र प्रचारबाजीको सहअस्तित्व हुन नसक्ने मात्र प्रस्ट्याउँदैन, यो वैयक्तिक अहंकारलाई भस्म पारेर सर्वतोभावेन विनीत बन्ने प्रक्रिया हो भन्नेतर्फ पनि संकेत गर्छ। लक्षित समुदाय भन्दा आफूलाई उच्च र विशिष्ट ठान्ने अनि विलासी जीवनशैली अपनाउन रुचाउनेले आफूलाई समाजसेवी भन्न सुहाउँदैन।</p><p style="margin-top: 15px; margin-bottom: 15px; text-align: justify; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;">यस परिप्रेक्ष्यमा, सर्वप्रथम स्वयं गैससहरू स्पष्ट हुनु जरूरी छ- उनीहरू समाजसेवी हुन् कि आत्मसेवी? गैससकर्मीहरू के हुन्- स्वयंसेवी कि तलबी कर्मचारी? समाजको समुन्नयनका लागि समर्पित रहेको दाबी त पर्याप्त गरिन्छ तर अन्तर्राष्ट्रिय दाताहरूले रकम प्रवाहित नगरे कति गैसस कायम रहलान्? पैसा नपाइने हो भने कति गैससकर्मी त्यो काम गर्न रुचाउलान्? यही सेरोफेरोमा उनीहरूका सामु खडा भएको ज्वलन्त यक्षप्रश्न हो– आफूमाथि लागेका आरोप र लाञ्छनाबाट उन्मुक्ति पाउन गैससहरूले के गर्नुपर्छ? यसको सही र स्पष्ट उत्तर गैसस आफैंले नठम्याएसम्म उनीहरूले विश्वासको संकटबाट मुक्ति पाउन सम्भव छैन। अहिलेसम्म त मर्यादा, नैतिकता, नियत र प्रतिष्ठामाथि लाञ्छनाको मात्र सामना गर्नुपरेको होला, तर भोलि त्यसका अन्य रूप पनि देखा पर्न सक्छन्।</p><p style="margin-top: 15px; margin-bottom: 15px; text-align: justify; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;"><strong>अरूले देख्नुपर्ने</strong><br>बिर्सन नहुने कुरा के हो भने यो असल छविका आधारमा काम गर्नुपर्ने क्षेत्र हो। गैससले राम्रो काम गरेर मात्र पुग्दैन, राम्रो नियतले काम गरेको पनि देखिनुपर्छ। आफूले देखेर हुँदैन, आफूसँग कहिल्यै सवालजवाफ गर्न नसक्नेहरूले त्यस्तो देख्न र महसूस गर्न सक्नुपर्छ। प्रत्येक कदममा, सधैभरि। कैयौं गैससहरूले इमान्दारीका साथ राम्रो काम गरेका छन्। उनीहरूका कारण समाजमा चेतना बढेको छ, विकृतिविरुद्ध लड्न सक्ने आत्मबल सिर्जना भएको छ, हेपिएका र पछि परेकाहरूमा आत्मविश्वास जागेको छ, तिनीहरू पुरुषार्थी र स्वावलम्बी बनेका छन्, तिनको व्यक्तित्व बढेको छ र समग्रमा सामाजिक रूपान्तरणलाई बल पुगेको छ।</p><p style="margin-top: 15px; margin-bottom: 15px; text-align: justify; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;">तर, त्यसको विपरीत, अनेकौं गैससहरूकै बेइमानी र गलत कार्यशैलीले गर्दा यस क्षेत्रले लाञ्छना पनि खप्नुपरेको छ। दोषीका कारण निर्दोषहरू बदनाम भएका छन् र निर्दोषलाई ढाल बनाएर दोषीहरू छाती फर्काउँदै हिंडेका छन्। यस्तो अवस्थामा, नगरेको गल्तीको आरोप खेप्नुपर्दा स्वच्छ गैससकर्मीहरूलाई चित्त दुख्नु अस्वाभाविक होइन। तिनलाई लाग्दोहोला, यति धेरै राम्रा काम भएका छन्– तिनको चर्चा किन हुँदैन र खराब कुराले मात्र किन चर्चा पाउँछन्? केहीले गलत काम गर्दा होलान् तर किन सबैलाई एकै घानमा पारिन्छ? किन डलर खेतीको आरोप लगाइन्छ?</p><p style="margin-top: 15px; margin-bottom: 15px; text-align: justify; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;">कारण अस्पष्ट छैन। पहिलो कुरा त, गैससलाई समाजसेवी संस्थाका रूपमा व्याख्या गरिने हुँदा समाजले उनीहरूबाट हाम्रो पारम्परिक मान्यता अनुुसारकै स्वच्छ एवं निस्वार्थ चरित्र र व्यवहारको अपेक्षा गर्छ। जब त्यस्तो देखिँदैन, आलोचना शुरू हुन्छ। जहाँसम्म डलर खेतीको प्रश्न छ, समाजले डलर खेती स्वयंलाई नराम्रो मान्दैन। उसले त डलर आर्जन गर्ने उद्योगी–व्यापारीहरू र विदेशबाट डलर कमाई पठाउने कामदारदेखि उच्च तहसम्मका व्यक्तिहरूलाई प्रशंसाकै दृष्टिले हेर्छ अनि बढीभन्दा बढी डलर ल्याउन र संचिति गर्न सक्ने सरकारप्रति गर्व नै गर्छ। समाजले त समाजसेवाको दम्भ गर्ने तर विदेशीका दान–दातव्यमा प्राण अड्काउने प्रवृत्तिलाई मात्र घृणा गरेको हो। सामाजिक रूपान्तरणको नारा दिने तर हरिया नोटलाई केन्द्रीभूत लक्ष्य बनाउने प्रवृत्तिलाई मात्र अवाञ्छनीय मानेको हो। यो त स्वागतयोग्य विषय हो, यसलाई अन्यथा लिने कामै छैन।</p><p style="margin-top: 15px; margin-bottom: 15px; text-align: justify; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;">समाजको दुर्दशा देखाएर पैसा थाप्ने र लटरपटर गरेर आफ्नो खल्तीमा हाल्ने प्रवृत्ति वाञ्छनीय हुनै सक्तैन। दोस्रो कुरा, गैससलाई कुनै व्यक्ति वा समुदायले प्रार्थना गरेर, हाम्रो उद्धार गरिदिनुपर्‍यो भनेर खडा हुन लगाएको होइन, बरु उनीहरू आफ्नै इच्छा र चाहनाले निस्वार्थ सेवा गर्छु भन्दै खडा भएका हुन्। स्वेच्छाले गर्ने कामको उपलब्धि त्यसको प्रभावकारिता र आत्मसन्तुष्टिमा खोजिनुपर्छ, अरूले गर्ने प्रशंसामा होइन। प्रशंसा हुनु राम्रो हो, त्यो बेग्लै कुरा। राम्रा कुराको प्रशंसा भएन भन्ने आधारमा, क्षतिपूर्ति खोजेझैं, नराम्रा कुराहरूको पनि चर्चा नभए हुन्थ्यो भन्ने अपेक्षा गर्न त झन् किमार्थ मिल्दैन। प्रशासनदेखि राजनीतिसम्मका विभिन्न क्षेत्रहरूबारे स्वयं गैससकर्मीहरूबीच नै के कस्ता टिप्पणीहरू हुने गर्छन् भनेर ऐना हेर्ने हो भने गैससबारे अरूका टिप्पणी स्वतः स्वाभाविक लाग्न थाल्नेछन्। तेस्रो कुरा, एउटा बिराउने, शाखा पिराउने उखान गैससका बारेमा मात्र अपवाद हुन सक्तैन। तर, सगोलमा जेजस्ता टिप्पणी भए पनि, जसले यथार्थमा स्वच्छ तवरले प्रभावकारी काम गरेका छन् त्यस्ता गैससले त्यस समुदायमा राम्रो आदर, सम्मान र स्नेह पनि त पाएका छन्। तीमाथि अरूले अनाहक आक्षेप लगाए भने समुदाय नै प्रतिरक्षामा उत्रने गरेको पनि त छ नि। यो सानो उपलब्धि हो र?</p><p style="margin-top: 15px; margin-bottom: 15px; text-align: justify; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;"><strong>पारदर्शी जवाफ</strong><br>सही उत्तर ठम्याउन नसक्ता संकट आइलाग्ने प्रश्न नै यक्षप्रश्न हो। गैससमाथि लाग्ने गरेका आरोप र लाञ्छना पनि उपर्युक्त यक्षप्रश्नको उत्तर ठम्याउन गरिएको धिङ्न्याइकै उपज हुन्। तीबाट उन्मुक्ति पाउन के गर्नुपर्छ भन्ने उपाय वस्तुतः तिनै आरोप र लाञ्छनाको गर्भमा निहित- एकदमै स्पष्ट, सरल एवं पारदर्शी। एउटै वाक्यमा भन्नुपर्दा- जुन काम अरूबाट छलेर–लुकाएर गर्नुपर्छ र कसैले थाहा पाउला भनेर सधैं डराउनुपर्छ त्यस्तो काम नगर्नु नै त्यो उपाय हो। यही हो यक्षप्रश्नको सही उत्तर। आफूले गर्न हुने तर अरूले थाहा पाउन नहुने कुनै पनि काम सामाजिक संस्थाको लक्ष्मणरेखाभित्र पर्दैन। गैससहरू यति सामान्य कुरा बुझ्न नसक्ने स्तरका पक्कै छैनन्। तर, सुतेकालाई ब्यूँझाउन सकिन्छ, ब्यूँझेकालाई उठाउन अरूको जोर चल्दैन। त्यसका लागि उसकै चाहना र इच्छाशक्ति आवश्यक हुन्छ।</p><p style="margin-top: 15px; margin-bottom: 15px; text-align: justify; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;">स्पष्ट छ, गैसस आत्मसेवी नभएर समाजसेवीका रूपमा अगाडि आउनुपर्छ। गैससकर्मी मूलतः स्वयंसेवी हुनुपर्छ। आफ्नो मात्र होइन परिवारको समेत रोजगारी चलाउने धन्दाबाट मुक्त हुनुपर्छ। गैससको नीति, नियम, सञ्चालन र व्यवस्थापन सबै पारदर्शी हुनुपर्छ। सरकार र अरूलाई दोष दिएर पानीमाथिको ओभानो बन्नेतिर होइन आफू के गर्दैछु भन्नेतिर अभिमुख हुनुपर्छ। सरकार पुग्न नसकेका ठाउँमा पुग्ने र उसले गर्न नसकेको सेवा प्रदान गर्नेतिर लाग्नुपर्छ। जनताको विश्वास एवम् आत्मीयता आर्जन गर्न हुरुक्क हुनुपर्छ। सरकारको परिपूरक र सहयोगी हुँदाहुँदै पनि सरकारको प्रत्यक्ष जिम्मेदारी रहेका आधारभूत आवश्यकताका क्षेत्रमा सोझै, प्रत्यक्ष फाल हाल्नबाट जोगिनुपर्छ।</p><p style="margin-top: 15px; margin-bottom: 15px; text-align: justify; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;">विकास कार्य र सामाजिक सेवाबीचको सीमारेखा पहिल्याउन सक्नुपर्छ। दाताहरूलाई एजेन्ट गैससको उत्पादन बन्द गरेर भएका गैसस मार्फत नै काम गर्न सहमत गराउनुपर्छ र नेपालमा जे पनि गर्न–गराउन सकिन्छ भन्ने धारणा बदल्न व्यवहारतः बाध्य बनाउन सक्नुपर्छ। केन्द्रीय गैससले फील्डमा आफैं प्रोजेक्ट चलाउने काम परित्याग गरेर समुदायमा आधारित गैससहरूलाई मात्र त्यसमा परिचालित गराउनुपर्छ र आफूलाई नीति बनाउने, लबिङ गर्ने, तालिम–सहयोग–अनुगमन गर्ने, जनशक्ति विकास गर्ने जस्ता कार्यमा केन्द्रित एवं सीमित गर्नुपर्छ। समुदायका गैससमा त्यहींका मानिसले मात्र सम्पूर्ण काम गर्ने व्यवस्था हुनुपर्छ, यतिमात्रले पनि भ्रष्टाचारको सम्भावना स्वात्तै घटाउँछ।</p><p style="margin-top: 15px; margin-bottom: 15px; text-align: justify; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;">रह्यो भाषाको कुरा। नेपालभित्र नेपालीले नेपालीका लागि चलाउने गैससहरू र तिनले गर्ने समग्र कामकाज, सूचना, विज्ञापन तथा सम्पूर्ण प्रकाशनहरू पूर्णतः नेपाली भाषामा मात्र सञ्चालन हुने परिपाटी शुरू गर्नैपर्छ। प्रपोजल र रिपोर्टलाई अबोधगम्य शब्दाडम्बर र जटिल फम्र्याटबाट मुक्ति दिलाएर सामान्य नेपाली लेखपढ गर्न सक्ने ग्रामीण नेपालीले पनि तयार गर्न सक्ने ढाँचामा ढाल्नैपर्छ। होइन भने जनताको सशक्तीकरण, पहुँच, पारदर्शिता जस्ता शब्द उच्चारण गर्ने ढोंग छोड्नुपर्छ। दाताहरूले नेपाली नबुझ्ने हुँदा उनीहरूसँग गरिने लेखापढी र व्यवहारमा अंग्रेजीको विकल्प नहोला, तर अंग्रेजीको प्रयोग एवम् उपयोग त्यत्तिका लागि र त्यही सीमाभित्र मात्र हुनुपर्छ। विदेशी दाताहरू र नेपाली संस्था तथा लक्षित समुदायबीच प्रत्यक्ष–अप्रत्यक्ष संवादका लागि छुट्टै अनुवादक संयन्त्र केन्द्रमा खडा गरिनुपर्छ। उनीहरू दुई–चारजना अनुवादक राख्नै नसक्ने गरीब त पक्कै छैनन् होला।</p><p style="margin-top: 15px; margin-bottom: 15px; text-align: justify; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;"><strong>आफ्नै रोजाइ</strong><br>बखतबहादुर हुने कि बलबहादुर, विनीता बन्ने कि देवकी, गैससकै रोजाइको विषय हो। जस्तो खेल खेल्न चाहने हो, त्यसका लागि मैदान खुला छ। समाजसेवाको संसारमा कर्म–फलको सिद्धान्तबाट छुटकारा पाउने कुनै उपाय छैन। बलबहादुरको काम गरेर बखतबहादुरको नाम कमाउने अथवा देवकीको गोरेटोमा हिंडेर विनीताको ठेगानामा पुग्ने चाहना राख्नु आफंैलाई धोका दिनु हो। त्यसले आफ्नो प्रशंसा आफैंले मात्र गरिहिँड्नुपर्ने र त्यो सुन्दा अरूलाई झिँझ्याट लाग्ने स्थितितर्फ धकेल्छ। बखतबहादुरको नाम खोज्ने हो भने काम पनि बखतबहादुरकै जस्तो गर्नुपर्छ। होइन भने, गैसस र तिनका क्रियाकलापप्रति समाजमा जति व्यापक एवं गहिरो असन्तुष्टि बढ्दैछ अनि प्रत्येक स्तरबाट जस्ता प्रतिक्रिया आउँदैछन् त्यसले कुनै दिन विस्फोटक रूप पनि लिन सक्छ। मुलुक अत्यन्त अस्थिर, तरल र अन्योलग्रस्त राजनीतिमा रुमल्लिंंदै गएको यो बेला असन्तुष्टिको यो अगुल्टो उपयोग गर्न कुनै गैरजिम्मेदार राजनीतिक शक्ति अघि बढ्यो भने त्यसबाट लाग्ने डढेलोले गैसस सञ्जाल मात्र होइन अरू धेरै थोक पनि ध्वस्त पार्न सक्छ। विचार त त्यहाँसम्म पुर्‍याउनुपर्ला नि!</p>', '2017-07-19 15:20:36', '2017-07-19 15:20:36'),
(27, 'सुरु भयो गोरखा दरबारको पुननिर्माण', 'community', '5.PNG', 'reconstruction-of-gorkha-palace', '2017-07-19', '<p style="margin-bottom: 15px; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;">भूकम्पबाट क्षति पुगेको ऐतिहासिक, पुरातात्विक, धार्मिक एवम् पर्यटकीय महत्वको गोरखा दरबारको पुनःनिर्माण थालिएको छ। दरबार भत्काएर जगैदेखि पुनःनिर्माण गर्न लागिएको हो।</p><p style="margin-top: 15px; margin-bottom: 15px; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;">गोरखा दरबार हेरचाह अड्डाका नायब सुब्बा रामेश्वर कट्टेलका अनुसार दरबारलाई चारैतिरबाट जस्ता पाताले घेरेर भत्काउन सुरु गरिएको छ। सो कार्यका लागि २० लाख रुपैया बजेट उपलब्ध भएको उनले बताए।</p><p style="margin-top: 15px; margin-bottom: 15px; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;">पुरातत्व विभागले जगैदेखि दरबार पुनःनिर्माण गर्न भनेपछि भत्काउन सुरु गरिएको हो। पुरातत्व विभागले पुनःनिर्माणका लागि पाँच करोड ५० लाख रुपैया उपलब्ध गराएको छ।&nbsp;<br>दरबारका मूल पूजारी प्रदीपनाथ योगीले नेपाल एकीकरणको थालनी भएको गोरखा दरबारलाई पुनःनिर्माण गरेपछि भावी सन्ततीलाई यसबारे जानकारी गराउन सकिने भन्दै खुशी व्यक्त गरे।भूकम्पले दरबारको गारो चर्कनुका साथै आँगन दबेको थियो।&nbsp;</p><p style="margin-top: 15px; margin-bottom: 15px; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;"></p><div class="cs_img " style="max-width: 100%; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px; width: 1800px;"><p style="margin-bottom: 15px;"><img src="https://nepalkhabar.com/get_img?ImageWidth=1800&amp;ImageHeight=1200&amp;ImageId=27438" alt="भूकम्पले क्षतिग्रस्त ऐतिहासिक गोरखा दरबारको जीर्णोद्वार सुरु गरिँदै" title="भूकम्पले क्षतिग्रस्त ऐतिहासिक गोरखा दरबारको जीर्णोद्वार सुरु गरिँदै" border="0" style="max-width: 100%; height: auto;"></p><p class="cs_img_caption" style="margin-top: 15px; margin-bottom: 15px; color: rgb(136, 136, 136); font-size: 14px !important;">भूकम्पले क्षतिग्रस्त ऐतिहासिक गोरखा दरबारको जीर्णोद्वार सुरु गरिँदै</p></div><span style="font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;">तत्कालीन राजा पृथ्वीनारायण शाहले २० वर्षको उमेरमा राज्याभिषेक गरेको गोरखा दरबारसँगै रहेको गोरखकाली मन्दिर (राजपरिवारको कुलदेवता), गोरखनाथ गुफा, रोट पोल्ने ठाउँ, चौधेरा दरबार (राजपरिवार आउँदा बस्ने ठाउँ), विद्यामन्दिर, पण्डित पाटी, पशुपतिनाथको मन्दिर, शीत्तल पाटीलगायतलाई भूकम्पले क्षति पु¥याएको थियो।</span><p style="margin-top: 15px; margin-bottom: 15px; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;"></p><p style="margin-top: 15px; margin-bottom: 15px; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;">यसैगरी, गोरखनाथ गुफास्थित मन्दिरको पुनःनिर्माण सकिएको छ। भूकम्पपछि एक महिनासम्म दरबारमा पूजाअर्चनाका लागि भक्तजनलाई रोक लगाइएको थियो। भूकम्पबाट क्षति पुगेका संरचना हटाएपछि अहिले निर्वाध रुपमा पूर्जाअर्चना हुँदै आएको छ। गारो चर्किएकाले जुनसुकै बेला भत्कन सक्ने खतरा थियो। भूकम्पपछि प्राप्त भएको ३६ लाख रुपैया बजेटमा भत्किएका केही संरचना पुनःनिर्माण गरिएका छन्।</p><p style="margin-top: 15px; margin-bottom: 15px; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;">दरबारको पुनःनिर्माण गर्न करिब तीन वर्ष लाग्ने छ। पुनःर्निर्माण गरिँदा पूजाआजा नरोकिने गरी व्यस्थापन गर्न लागिएको छ।</p>', '2017-07-19 15:23:57', '2017-07-19 15:23:57'),
(28, '५० थान गोलीसहित दुई पक्राउ', 'community', 'st4.PNG', 'caught-with-50-round-bullet', '2017-07-19', '<p style="margin-bottom: 15px; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;">भारतबाट अवैध रुपमा भित्र्याइएका ५० थान गोलीसहित पक्राउ परेका दुईजना अभियुक्तलाई आज जिल्ला प्रहरी कार्यालय पर्साले सार्वजनिक गरेको छ। बाह्र बोरको ५० थान गोलीसहित पक्राउ परेका पर्साको बगवाना–७ निवासी ५५ वर्षका क्षेत्री चौधरी र २१ वर्षका रामकिशुन चौधरीलाई प्रहरीले सार्वजनिक गरेको हो।</p><p style="margin-top: 15px; margin-bottom: 15px; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;">ना ४५ प ९१९६ नम्बरको मोटरसाइकलमा सवार रहेका चौधरीद्वयले आलुको बोरामा गोली लुकाइछिपाई नेपाल भित्र्याउन लागेको पूर्व सूचनाको आधारमा पर्साको मनवा र रंगपुर टाँडीको बीचमा जाँच गर्दा १२ बोरको ५० थान गोली बरामद भएपछि आवश्यक अनुशन्धानका लागि नियन्त्रणमा लिइएको पर्साका प्रहरी उपरीक्षक सुदीप गिरीले जानकारी दिए।</p><p style="margin-top: 15px; margin-bottom: 15px; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;">जिल्ला प्रहरी कार्यालय पर्साले आयोजना गरेको पत्रकार सम्मेलनमा उपरीक्षक गिरीले पक्राउ परेका दुबै अभियुक्तमाथि हातहतियार तथा खरखजाना मुद्दा चलाउने तयारी भएको बताए।</p><p style="margin-top: 15px; margin-bottom: 15px; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;">पक्राउ परेका क्षेत्री चौधरी वन पैदवार चोरी निकारी, वन रक्षकमाथि आक्रमण जस्ता अपराधमा पहिले पनि मुद्दा खेपिसकेको उपरीक्षक गिरीले जानकारी दिए। रासस</p>', '2017-07-19 15:26:18', '2017-07-19 15:26:18');
INSERT INTO `articles` (`id`, `title`, `category`, `userfile`, `slug`, `pubdate`, `body`, `created`, `modified`) VALUES
(29, '‘एमालेको विश्लेषणः प्रत्यक्षमा दुई तिहाइ, समग्रमा बहुमत’', 'politics', '6.PNG', 'uml_bisesh', '2017-07-20', '<p style="margin-bottom: 15px; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px; text-align: justify;">स्थानीय तहको दुई चरणको निर्वाचनमा नेकपा एमालेले पहिलो स्थान हासिल गरेको छ। संविधान संशोधनबारे सहमति हुन नसकेका कारण दुई नम्बर प्रदेशको निर्वाचन तेस्रो चरणमा (असोज २ गते) तोकिएको छ। दोस्रो चरण निर्वाचनको समीक्षा, तेस्रो चरणको तयारी र संविधान संशोधनको सेरोफेरोमा एमाले स्थायी समिति सदस्य&nbsp;<strong>शंकर पोखरेल</strong>सँग नेपालखबरका लागि&nbsp;<strong>सङ्गीत श्रोता</strong>ले गरेको वार्ताको सम्पादित अंशः</p><hr style="margin: 15px 0px; border-top-color: rgb(221, 221, 221); font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;"><p style="margin-top: 15px; margin-bottom: 15px; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px; text-align: justify;"><strong>स्थानीय तहको दुवै चरणको निर्वाचन–परिणामलाई एमालेले कसरी मूल्याङ्कन गरेको छ?</strong><br>संविधान कार्यान्वयनका लागि यो चुनाव अपरिहार्य थियो। सत्तारूढ पार्टीहरू संविधान संशोधन नभई चुनाव हुन सक्दैन भन्ने तर्क गर्दै थिए। हाम्रा लागि स्थानीय तह निर्वाचनका सवालमा संविधान संशोधनको प्रसंग नै अनुचित भन्ने हाम्रो विचार रहेको थियो। दुवै चरणको निर्वाचन सफलताले संविधान संशोधनलाई स्थानीय तह निर्वाचनसँग जोड्ने कोशिस असान्दर्भिक भएको छ। खासगरी मधेसमै पनि ठूलो संख्याका मतदाताहरूले निर्वाचनमा सहभागिता जनाएका छन्। मधेस केन्द्रित दल र मधेस केन्द्रित दलका कार्यकर्ता पनि ठूलो संख्यामा निर्वाचनमा सामेल भए। यसले संविधान संशोधन नभई चुनाव हुन सक्दैन भन्ने तर्क खण्डित गरेको छ।</p><p style="margin-top: 15px; margin-bottom: 15px; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px; text-align: justify;">दोस्रो, नेकपा एमाले निर्वाचनबाट पहिलो पार्टीका रूपमा स्थापित हुन सफल भएको छ। राष्ट्रिय राजनीतिका सवालमा एमालेले लिँदै आएका अडानहरू मुलुकको हितमा छन्, छैनन् भन्ने बहस चलिरहेको थियो, त्यसमा जनमतको बढी हिस्सा एमालेका पक्षमा देखा परेको छ। राष्ट्रिय राजनीतिमा एमालेले लिएको अडानलाई जनताले स्वीकारेका छन्।</p><p style="margin-top: 15px; margin-bottom: 15px; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px; text-align: justify;">तेस्रो, एमाले नेतृत्वमा भएको भारतीय नाकाबन्दीको सामना, चीनसँग ऐतिहासिक सम्बन्ध विस्तार र आर्थिक सम्झौता, नीति कार्यक्रम र बजेटबाट जनता र राष्ट्रको पक्षमा काम आदिका कारण यो पार्टीले पहाडमा त राम्रै गर्ला तर तराईमा बढारिन्छ भन्ने प्रचार चलाइएको थियो। त्यसलाई पनि चुनावको परिणामले खण्डित गरेको छ। तराईका कञ्चनपुर, कैलाली, बाँके, दाङजस्ता जिल्लामा एमालेले उल्लेख्य सफलता हासिल गरेको छ। टीकापुर घटनासँग जोडेर एमाले थारूविरोधी, मधेसविरोधी भन्ने प्रचार गरियो, तर थारू बाहुल्यता रहेको टीकापुरमा एमालेले चुनाव जितेर त्यो तर्कलाई गलत सावित गरेको छ। एमाले मधेसविरोधी हो भनेर गरिएको प्रचारलाई मधेसकै जनताहरूले अस्विकार गरेका छन्।</p><p style="margin-top: 15px; margin-bottom: 15px; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px; text-align: justify;"><strong>दुई नम्बर प्रदेशमा त निर्वाचन हुन बाँकी नै छ, त्यहाँ कस्तो तयारी गर्नुभएको छ?</strong><br>दुई चरणको निर्वाचनले एमाले लोकप्रिय रहेछ भन्ने पुष्टि भइसक्यो। जनताले एमालेलाई पहिलो पार्टीका रूपमा स्थापित गरेका छन्। हामीले दुई नम्बर प्रदेशमा हुने तेस्रो चरणको निर्वाचनमा पनि यसलाई कायम राख्छौं र पहिलो पार्टी हुने विश्वासमा छौं। दुई नम्बरमा कम्तीमा हामी नेपाली कांग्रेसभन्दा माथि नै हुन्छौं। त्यही प्रकारको तयारीमा हामी जुटिरहेका छौं। पछिल्लो समय मधेसकेन्द्रित दलहरू आफैं एमालेलाई एक्लाएर ठीक काम गरिएन भन्ने ठाउँमा पुग्नुभएको छ। एमाले बरू स्पष्ट रहेछ, दोहोरो कुरा त सत्तापक्षले गर्ने रहेछ भन्ने उहाँहरूलाई परेको छ। त्यसकारण हामी आफ्ना कुरा राखेर मधेसी जनताकै विश्वासमा एमालेलाई दुई नम्बरमा पनि पहिलो पार्टी बनाउने योजनामा छौं।</p><p style="margin-top: 15px; margin-bottom: 15px; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px; text-align: justify;"><strong>संविधान घोषणापछि मधेसप्रति सबैभन्दा नकारात्मक भूमिका र टीकाटिप्पणी एमालेबाटै भएको देखिन्थ्यो। संविधान संशोधनमा अहिले पनि एमाले तयार छैन। यसको असर तेस्रो चरणको निर्वाचनमा पर्छ भन्नेहरू छन् नि?</strong>&nbsp;<br>त्यस्तो हुन्थ्यो भने यो तर्क पाँच नम्बर र सात नम्बरको तराईका जिल्लाका हकमा पनि लागू हुन्थ्यो। ती ठाउँमा एमालेले चुनाव जित्ने थिएन। दुई नम्बर प्रदेश अलिकति पछाडि परेका कारण यसलाई हामीले ध्यानमा राख्नुपर्छ। तराई मधेसका सीमान्तकृत समुदाय, त्यहाँका मजदुर किसान। गरिब, महिला, दलितका पक्षमा निरन्तर आवाज उठाउँदै आयौं। कम्युनिस्ट पार्टीको परम्पराका हिसाबले पार्टी स्थापनापछि नै ठूलो किसान आन्दोलन तराई मधेसबाटै उठेको थियो। त्यहाँका सामन्त ठालूहरूको परम्पराविरूद्धको आन्दोलनमा नेतृत्व नै कम्युनिस्ट पार्टीले गरेको हो। अहिले पनि मधेसमा जुन प्रकारको सामन्ती प्रभाव र ठूलाठालुहरू हेजेमोनी छ, त्यो चिर्न एमाले योजनाका साथ अघि बढिरहेका कारण उनीहरूले साम्प्रदायिकताको रंग दिएर एमालेलाई पहाडियाको पार्टी र मधेसविरोधी पार्टी भने। यसको अर्थ त्यहाँभित्र चलिरहेको वर्गसंघर्षलाई अलग गर्न सकिन्छ भन्ने नियत देखिन्छ। मधेसका श्रमजिवी वर्ग, दलित, मुस्लिम, थारू समुदायको बहुसंख्यक हिस्सा एमालेप्रति आकर्षित छ। यसलाई कोर मधेसका मधेसी समुदायका नेताहरूले पनि महसुस गर्न थालेका छन्। पछिल्लो समय बारा, पर्सा, रौतहटदेखि लिएर सिरहा सप्तरीसम्म माओवादी र मधेसी दलबाट एमालेमा प्रवेश गर्ने लहर चलेको छ। हाम्रा नेताहरूलाई दिनहुँ नयाँनयाँ व्यक्ति प्रवेश गराउन भ्याइनभ्याइ छ। त्यस हिसाबले एमाले तराई–मधेसमा कमजोर भन्ने तर्क सही होइन।</p><p style="margin-top: 15px; margin-bottom: 15px; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px; text-align: justify;"><strong>बितेको दुई वर्ष मधेस र एमालेबीच दूरी एकदम बढेकै देखिन्थ्यो तर दुई नम्बर प्रदेशको निर्वाचनको मुखैमा एमाले र मधेसी दलहरूसँग संवाद चलिरहेको सुनिन्छ। कतिले त एमाले र मधेसकेन्द्री दलबीच चुनावी तालमेलको समेत गृहकार्य भइरहेको भन्छन् नि, यो सत्य हो?</strong><br>आउने दिनमा नेपाली राजनीतिमा दुई धारबीच टक्कर हुने देख्छु म। एउटा पुँजीवादी धार, जसको नेतृत्व नेपाली कांग्रेसले गर्छ र अर्को समाजवादी धार, जसको नेतृत्व एमालेले गर्छ। यो दुई धारलाई केन्द्रबिन्दु बनाएर धु्रवीकरण सुरू हुन्छ। त्यस हिसाबले हाम्रो धारलाई स्वागत गर्न कोही तयार हुन्छ भने एमालेले स्वागत गर्छ। मधेसकेन्द्रित दलहरूमध्ये कसैले आफूलाई समाजवादी धारको नजिक ठान्छ भने हामीले निषेध गर्ने कुरै भएन। एमाले मधेसको गरिबी र असमानता अन्त्य गर्ने सवालमा सबैभन्दा प्रतिबद्ध पार्टी हो। मधेसप्रति गरेको प्रतिबद्धता र मधेसको चिन्तालाई देखेर नै एमालेलाई मधेसविरोधी आरोप लगाइएको हो। त्यस परिप्रेक्ष्यमा हामी उहाँहरूसँगै सहकार्य गरेर वास्तविक कुरा जनताबीच पुर्‍याउन चाहन्छौं।</p><p style="margin-top: 15px; margin-bottom: 15px; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px; text-align: justify;"><strong>राष्ट्रिय जनता पार्टी (राजपा)ले संविधान संशोधनको मुद्दा पनि जोडका साथ उठाएको छ। तेस्रो चरणको निर्वाचनमा एमालेले राजपासँगै चुनावी सहकार्य गर्न खोजेको हो?</strong><br>अहिले नै त हामी त्यस तहमा पुगेका छैनौं। दुई नम्बर प्रदेशको निर्वाचनका लागि वातावरण बनाउनेमै हाम्रो जोड छ। फेरि निर्वाचनका सन्दर्भमा गठबन्धन नै हुन सक्दैन भन्ने होइन। स्थानीय तहको चुनावमा मधेसी जनाधिकार फोरम (लोकतान्त्रिक)सँग विराटनगरमा तालमेल भएकै थियो। संघीय समाजवादी फोरमसंग बाँँकेमा जिल्ला समन्वय समिति निर्वाचनमा तालमेल भइरहेको छ। त्यसकारण तालमेल हुनै नसक्ने भन्ने छैन। हामी आफ्ना एजेण्डा छाडेर कसैसंग तालमेल गर्न जाँदैनौं, तर हाम्रो एजेण्डासँग सहकार्य गर्न कोही आउँछ भने हामीले त्यसलाई अस्वीकार गर्ने भन्ने हुँदैन। मधेसकेन्द्रित दलहरूले मधेसको विभेद, असमानता, गरिबी र पछौटेपनविरूद्ध एमालेसँग सहकार्य गर्न चाहन्छन् भने हामी त्यसलाई स्वागत गर्छौँ। किनभने हाम्रो मिसन नै त्यही हो। तर, त्यसका नाममा संविधान कार्यान्वयन गर्न नदिने, नेपाल राष्ट्र कमजोर पार्ने, त्यसैका नाममा राजनीतिक अस्थिरता लम्ब्याइरहन चाहने प्रवृत्तिसँग हाम्रो भने असहमति हो।</p><p style="margin-top: 15px; margin-bottom: 15px; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px; text-align: justify;"><strong>राजपा संविधान संशोधन नभएसम्म निर्वाचनमा नजाने अडानमा कायमै छ। एमाले चाहिँ तेस्रो चरणको निर्वाचनअघि संशोधनका लागि लचक हुँदैछ कि?</strong><br>स्थानीय चुनावका लागि संविधान संशोधन जरूरी छैन। स्थानीय चुनाव सकौं, त्यसपछि छलफल गर्न सकिन्छ। यद्यपि अहिले आएको संशोधनप्रति हाम्रो सहमति छैन। राष्ट्रको हितलाई कसरी सुनिश्चित गर्ने भन्नेमा उहाँहरू पनि तयार हुने हो भने तराई–मधेसका जनताका हितको पक्षमा उभिन हामीलाई गाह्रो छैन। तर तराई मधेसको हितका नाममा राष्ट्रको हित कमजोर पार्ने कुरा स्वीकार्य छैन। जस्तैः अंगीकृत नागरिकको बाढी प्रवेश गराउनुको अर्थ रैथाने मधेसीको अधिकार खोसिनु हो। जति अंगीकृतको संख्या र तागत बढ्छ रैथाने मधेसी उति कमजोर हुन्छन्। हाम्रो संविधानले समावेशीतालाई त स्वीकार गरेकै छ। भारतीय आप्रवासनबाट आएर जति मान्छे अंगीकृत बन्छन्, त्यसले भाषा, परम्परा, रितिरिवाज मिल्नेहरूको अधिकार खोसिन्छ। उनीहरू आउनासाथ आफूलाई मधेसीकरण गरिहाल्छन्, पहाडीकरण गर्न त उनीहरूलाई गाह्रो छ। त्यस अर्थमा मधेसी जनताको चिन्ता र सरोकारका विषयमा सबैभन्दा प्रष्टसँग कुरा उठाउने काम एमालेले नै ग¥यो। हामीले त रैथानै मधेसीकै पक्षमा आफूलाई उभ्याएका हौं, अंगीकृतको आलोचना गरेका हौं। यो पाटोलाई सबैले बुझ्नु जरूरी छ।</p><p style="margin-top: 15px; margin-bottom: 15px; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px; text-align: justify;"><strong>दोस्रो चरणसम्मको परिणामको मूल्यांकन गरेर एमालेले २५ स्थान आन्तरिक कारणले गुमाएको निष्कर्ष निकाल्यो। एमालेभित्र दुई समूहबीच अझै भावनात्मक एकता नभइसकेको हो?</strong><br>सिट गुमाउनुका दुई तीनवटा कारण छन्। एमाले केन्द्रीय रूपमा एजेण्डागत हिसाबले सबैभन्दा ज्यादा एकताबद्ध पार्टी हो। चुनावको परिणामले पनि त्यसलाई पुष्टि गरेको छ। तर केही ठाउँमा पार्टीभित्रको एकता स्थानीय तहमा प्रतिबिम्बित हुन सकेन। केही ठाउँमा पार्टीको आन्तरिक कारणले पनि परिणाम प्रतिकूल भए। ठूलो संख्याको अन्तरमा उपमेयरले चुनाव जित्ने मेयर हार्ने, ठूलो संख्याको अन्तरमा उपाध्यक्षले जित्ने अध्यक्षले हार्ने जस्ता प्रवृत्तिहरू विभिन्न ठाउँमा देखिए। यसका केही कारण छन्ः स्थानीय तहमा पार्टीबीचको आन्तरिक एकताको पक्ष कमजोर रहनु, आकांक्षीहरूको आकांक्षा सही व्यवस्थापन गर्न नसक्नु आदि। अर्कोतर्फ उपयुक्त निर्वाचन व्यवस्थापन रणनीति अख्तियार गर्न नसक्दा पनि समस्या भए। जहाँ अस्वाभाविक प्रकारका परिणाम निस्किए, त्यसको हामी समीक्षा गर्दैछौँ। त्यसबाट सिकेर भविष्यमा यस्ता घटना नदोहोरिने सुनिश्चितता गर्न चाहन्छौँ। यद्यपि यो मुख्य प्रवृत्ति चाहिँ होइन।</p><p style="margin-top: 15px; margin-bottom: 15px; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px; text-align: justify;"><strong>दुई चरणको स्थानीय निर्वाचन परिणामले एमालेलाई पहिलो शक्तिका रूपमा स्थापित गरेको छ। आउँदा निर्वाचनमा पनि यही परिणाम कायम रह्यो भने एमाले पहिलो पार्टी बन्ने तर सरकार चाहिँ दोस्रो र तेस्रो पार्टीको गठबन्धनले चलाइरहने हुने त होइन?</strong>&nbsp;<br>त्यस्तो त देखिँदैन। किनकी एमाले आउने निर्वाचनबाट स्पष्ट बहुमत ल्याउने अवस्थामा छ। स्थानीय चुनावमा स्थानीय प्राथमिकताहरूले काम गर्छ। अहिले हामीले प्राप्त गरेको मत यथावत राख्न सक्दा प्रदेश वा संघको निर्वाचनमा हामीले प्रत्यक्षतर्फ झण्डै दुई तिहाइको हाराहारीमा चुनावमा जित्ने सम्भावना देखिन्छ। त्यसलाई समानुपातिकतर्फ जोड्दा सहज बहुमतको अवस्था हुनेछ। त्यसैले प्रत्यक्ष र समानुपातिक मिलाउँदा सहज बहुमत प्राप्त गर्न सकिन्छ भन्ने नै हाम्रो विश्लेषण छ। अहिलेको गठबन्धन चुनाव पछाडि रहन सक्ने तागत उहाँहरूसँग रहन्छ भन्ने मलाई लाग्दैन।</p><p style="margin-top: 15px; margin-bottom: 15px; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px; text-align: justify;"><strong>चुनाव अगाडि नै सरकारमा जाने एमालेको कुनै योजना छ कि?</strong><br>त्यस्तो हाम्रो चाहना छैन। नेपालको राजनीतिमा सत्तामा रहे मात्र चुनाव जितिन्छ भन्ने मान्यता थियो। त्यो मानसिकता अन्त्य हुनुपर्छ भनेर हामीले यसपटक सत्ता बाहिरै रहेर चुनाव लड्नेमा जोड दियौं र त्यसलाई हामीले पुष्टि पनि गरेका छौं। यद्यपि सत्तापक्षले सत्ताको दुरूपयोग नगरेको भए यो भन्दा राम्रो परिणाम ल्याउथ्यौं हामीले। तर प्रतिपक्षमा बस्नेबित्तिकै चुनावमा पराजय बेहोर्नुपर्छ भन्नेचाहिँ कमजोर मानसिकता हो। लोकतन्त्रको बलियो आधार निर्माणमा यो कुरा महत्वपूर्ण छ भन्ने म ठान्छु।</p>', '2017-07-20 19:01:19', '2017-07-20 19:01:19'),
(31, 'नेपालद्वारा सिंगापुर ४ विकेटले पराजित', 'sports', '8.PNG', 'nepal_singapur_cricket', '2017-07-20', '<p style="margin-bottom: 15px; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;">सिंगापुरमा जारी आइसीसी यु–१९ विश्वकप क्रिकेटको एसियन छनोटको पहिलो खेलमा नेपालले घरेलु टोली सिंगापुरलाई ४ विकेटले पराजित गरेको छ। सन् २०१८ मा न्यूजिल्याण्डमा हुने आइसीसी यु–१९ विश्वकप क्रिकेटको छनौटको पहिलो खेलमा घरेलु टोली सिंगापुरले दिएको ७४ रनको लक्ष्य नेपालले १७ दशमलब ३ ओभरमा ६ विकेटको क्षतिमा भेट्टाएको हो।</p><div class="_4u-c _1wfr _9hq" style="font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;"><div class="_5f0v uiScrollableArea fade contentBefore"><div id="js_r" class="uiScrollableAreaWrap scrollable"><div class="uiScrollableAreaBody"><div class="uiScrollableAreaContent"><div class="__i_"><div id="js_1"><div class="_1t_p clearfix"><div class="_41ud"><div class="clearfix _o46 _3erg _29_7 direction_ltr text_align_ltr"><div id="js_23" class="_3058 _ui9 _hh7 _s1- _52mr _3oh-" data-tooltip-content="10:57" data-hover="tooltip" data-tooltip-position="left"><div class="_aok">नेपालका लागि प्रनित थापा मगरले सर्वाधिक २३ रनको योगदान दिए। यस्तै, अनिल शाहले १९ रन जोड्दा रोहित कुमार पौडेलले ११ र पवन सराफ ४ रनमा अविजित रहे। सुरुवाती व्याट्सम्यान सन्दिप सुनार १ रन र आरिफ सेख ४ रनमा पेभिलियन फर्केका थिए।</div><div class="_aok">&nbsp;</div><div class="_aok">यसअघि टस हारेर पहिले ब्याटिङको निम्तो पाएको घरेलु टोली सिंगापुरले २९ ओभरमा अलआउट हुँदै ७३ रनको योगफल तयार पारेको थियो। नेपाली बलरसामु निरीह साबित भएको घरेलु टोली सिंगापुरका लागि ब्रिगु लायरले सर्वाधिक ४२ बनाएका थिए। यस्तै, सेन अग्रवालले ११ रन बनाउँदा अन्य व्याट्सम्यानले दोहोरो अंक नै जोड्न सकेनन्।</div><div class="_aok">&nbsp;</div><div class="_aok">सिंगापुरका कप्तान जनक प्रकाश, आर्यमान सुनिल र रोहन रंंगराजनले खाता नै खोल्न सकेनन्। सिंगापुरलाई सस्तैमा समेट्न नेपालका लागि कप्तान सन्दिप लामिछानेले सर्वाधिक ५ विकेट लिए भने साहब आलमले ३ तथा किशोर महतोले २ विकेट लिएका थिए।</div><div class="_aok">&nbsp;</div><div class="_aok">एसियन छनौटमा शीर्ष चार राष्ट्र नेपाल, सिंगापुर, अफगानिस्तान र मलेसियाबीच प्रतिस्पर्धा हुनेछ। यी चार टोली मध्ये शीर्ष स्थानमा रहन सफल टोली सन् २०१८ मा न्यूजिल्याण्डमा हुने यु–१९ विश्वकप क्रिकेटका लागि छनोट हुनेछ।</div></div></div></div></div></div></div></div></div></div></div></div>', '2017-07-20 19:07:12', '2017-07-20 19:07:12'),
(32, 'त्रिशूली अस्पतालमा चिकित्सक नहुँदा प्रसूतिको शल्याक्रिया बन्द', 'health', '111.PNG', 'trishuli_hospital_got_problem_due_to_lack_of_doctors', '2017-07-20', '<p style="margin-bottom: 15px; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;">जिल्लाको एक मात्र सरकारी अस्पतालमा विशेषज्ञ चिकित्सक नहुँदा प्रसूतिको शल्याक्रिया सेवा एक सातादेखि बन्द भएको छ। त्रिशूली जिल्ला अस्पतालमा प्रसूतिका क्रममा जटिलता देखिएमा शल्यक्रिया गराउने विशेषज्ञ चिकित्सक नहुँदा सेवा बन्द भएको अस्पतालले जनाएको छ।</p><p style="margin-top: 15px; margin-bottom: 15px; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;">&nbsp;सामान्य प्रसूति गराउने सेवा बन्द नभए पनि शल्यक्रिया सेवा बन्द भएका कारण प्रसूतिका समयमा जटिलता उत्पन्न हुन सक्ने भयले गर्भवती राजधानी धाउन बाध्य भएको त्रिशूली अस्पतालका चिकित्सक डा राजेन्द्र लामाले बताए।</p><p style="margin-top: 15px; margin-bottom: 15px; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;">नुवाकोट, रसुवा र धादिङको उत्तरी क्षेत्रका बिरामीको दैनिक उपचार तथा प्रसूति सेवा प्रदान गर्दै आएको जिल्ला अस्पतालमा चिकित्सक नहुंँदा प्रसूति सेवा बन्द भएपछि स्वास्थ्य मन्त्रालयलाई ध्यानाकर्षण गराइएको विदुर नगरपालिकाका प्रमुख सन्जु पण्डितले बताए।</p><p style="margin-top: 15px; margin-bottom: 15px; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;">ेअस्पतालमा दैनिक १२÷१५ सुत्केरी हुने गथ्र्याे ।&nbsp;</p><div style="font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;"><p class="popPara" style="margin-bottom: 15px;">प्रसूति सेवा बन्द भएपछि प्रसूति विशेषज्ञ पठाइदिन प्रमुख पण्डितले स्वास्थ्य मन्त्रालयका सचिव डा किशोर रेग्मीसँग माग गर्नुभएको छ । आज अस्पतालको निरीक्षण गर्न आएको मध्यमाञ्चल क्षेत्रीय स्वास्थ्य निर्देशनालयका डा वासुदेव पाण्डेको टोलीलाई अस्पतालमा दुई प्रसूति विशेषज्ञ पठाउन माग गरिएको पण्डितले बताउनुभयो ।&nbsp;<br>विगतमा एक मात्र विशेषज्ञ चिकित्सक भएकाले सेवा बेला बेला प्रभावित हुने गरेको थियो । डा पाण्डेको टोलीले जिल्ला अस्पतालमा पुगेर नगर प्रमुख पण्डित, अस्पताल विकास समिति, अस्पताल सरोकार समिति तथा अस्पताल प्रशासन र चिकित्सकसंँग अन्तरक्रिया गरी छिटोभन्दा छिटो प्रसूति विशेषज्ञ पठाइदिने आश्वासन दिएको अस्पतालका डा लामाले जानकारी दिनुभयो ।&nbsp;<br>अस्पतालमा कार्यरत प्रसूति विशेषज्ञ डा विनम्र सिलवाल काज अवधि पूरा भएर फर्किएपछि उहाँको स्थानमा मन्त्रालयले कोही पनि नपठाउंँदा एक सातादेखि समस्या आएको हो । चार दशक पुरानो ५० शय्याको नुवाकोट जिल्ला अस्पताल त्रिशूलीमा १७ चिकित्सकको दरबन्दी रहेकामा दुई मात्र स्थायी दरबन्दीका चिकित्सक रहेको अस्पताल विकास समितिका अध्यक्ष पारस अधिकारीले जानकारी दिनुभयो ।रासस&nbsp;</p></div>', '2017-07-20 22:35:00', '2017-07-20 22:35:00'),
(33, 'यो काठमाडौं हो कि तालमाडौं?', 'community', '12.PNG', 'kathmandu_or_lakemandu', '2017-07-20', '<span style="font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;">महानगरका सडकमा खाल्टाखुल्टी त गनिसाध्य नै छैन। अझ बर्षादमा पानी र हिलो जमेका ठाउँहरु त कति हो कति। देशको राजधानी सहरमा बिहानै देखिएका यस्ता दृष्यले हाम्रो अव्यवस्थापनको चरम् नमूना प्रस्तुत गर्छन्। त्यसैले त महानगरवासी भन्छन्- अहिले त यो ठाउँ तालमाडौं बनेको छ।</span><span style="font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;">महानगरका सडकमा खाल्टाखुल्टी त गनिसाध्य नै छैन। अझ बर्षादमा पानी र हिलो जमेका ठाउँहरु त कति हो कति। देशको राजधानी सहरमा बिहानै देखिएका यस्ता दृष्यले हाम्रो अव्यवस्थापनको चरम् नमूना प्रस्तुत गर्छन्। त्यसैले त महानगरवासी भन्छन्- अहिले त यो ठाउँ तालमाडौं बनेको छ।</span><span style="font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;">महानगरका सडकमा खाल्टाखुल्टी त गनिसाध्य नै छैन। अझ बर्षादमा पानी र हिलो जमेका ठाउँहरु त कति हो कति। देशको राजधानी सहरमा बिहानै देखिएका यस्ता दृष्यले हाम्रो अव्यवस्थापनको चरम् नमूना प्रस्तुत गर्छन्। त्यसैले त महानगरवासी भन्छन्- अहिले त यो ठाउँ तालमाडौं बनेको छ।</span><span style="font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;">महानगरका सडकमा खाल्टाखुल्टी त गनिसाध्य नै छैन। अझ बर्षादमा पानी र हिलो जमेका ठाउँहरु त कति हो कति। देशको राजधानी सहरमा बिहानै देखिएका यस्ता दृष्यले हाम्रो अव्यवस्थापनको चरम् नमूना प्रस्तुत गर्छन्। त्यसैले त महानगरवासी भन्छन्- अहिले त यो ठाउँ तालमाडौं बनेको छ।</span><span style="font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;">महानगरका सडकमा खाल्टाखुल्टी त गनिसाध्य नै छैन। अझ बर्षादमा पानी र हिलो जमेका ठाउँहरु त कति हो कति। देशको राजधानी सहरमा बिहानै देखिएका यस्ता दृष्यले हाम्रो अव्यवस्थापनको चरम् नमूना प्रस्तुत गर्छन्। त्यसैले त महानगरवासी भन्छन्- अहिले त यो ठाउँ तालमाडौं बनेको छ।</span><span style="font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;">महानगरका सडकमा खाल्टाखुल्टी त गनिसाध्य नै छैन। अझ बर्षादमा पानी र हिलो जमेका ठाउँहरु त कति हो कति। देशको राजधानी सहरमा बिहानै देखिएका यस्ता दृष्यले हाम्रो अव्यवस्थापनको चरम् नमूना प्रस्तुत गर्छन्। त्यसैले त महानगरवासी भन्छन्- अहिले त यो ठाउँ तालमाडौं बनेको छ।</span><span style="font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;">महानगरका सडकमा खाल्टाखुल्टी त गनिसाध्य नै छैन। अझ बर्षादमा पानी र हिलो जमेका ठाउँहरु त कति हो कति। देशको राजधानी सहरमा बिहानै देखिएका यस्ता दृष्यले हाम्रो अव्यवस्थापनको चरम् नमूना प्रस्तुत गर्छन्। त्यसैले त महानगरवासी भन्छन्- अहिले त यो ठाउँ तालमाडौं बनेको छ।</span><br>', '2017-07-20 22:38:10', '2017-07-20 22:38:10'),
(34, 'चीनको कम्युनिष्ट पार्टीले गर्‍यो २ लाख १० हजार जनालाई अनुशासन कारबाही', 'world', '13.PNG', 'china_comunist_party_punished_2lakh_people', '2017-07-20', '<p style="margin-bottom: 15px; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;">कम्युनिष्ट पार्टी अफ चाइना (सिपीसी)का उच्च अनुशासन निरीक्षकका अनुसार दुई लाख १० हजार जना अधिकारीलाई अनुशासन उल्लङ्घनको कारबाही भएको छ। सन् २०१७ को छ महिनाभित्र यति ठूलो सङ्ख्यामा अनुशासन उल्लङ्घनको सजाय भएको सिपीसीले जानकारी दिएको हो।</p><p style="margin-top: 15px; margin-bottom: 15px; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;">यो तथ्याङ्क कम्युनिष्ट पार्टी अफ चाइनाको अनुशासन समिति (सिसिडिआइ)ले बिहीबार सार्वजनिक गरेको बेइजिङबाट प्राप्त समाचारमा जनाइएको छ।</p><p style="margin-top: 15px; margin-bottom: 15px; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;">यस प्रकार अनुशासनको सजाय भोगेका व्यक्तिहरुमा ३८ जना प्रान्तीय अर्थात मन्त्रीस्तरीय र एक हजारभन्दा बढी जिल्लास्तरका तथा आठ हजार चार सयभन्दा बढी गाउँस्तरका अधिकारी रहेका छन्।</p><p style="margin-top: 15px; margin-bottom: 15px; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;">सिपीसीले सन् २०१६ मा चार लाख १५ हजार व्यक्तिलाई अनुशासन उल्लङ्घनको कारबाही गरेको थियो। एजेन्‍सी</p>', '2017-07-20 22:41:14', '2017-07-20 22:41:14'),
(35, 'डा. कमला सांकृत्यायनको बधुशिक्षा–विश्लेषण', 'art-literature', '14.PNG', 'litrature_dr_kamala', '2017-07-20', '<p style="margin-bottom: 15px; text-align: justify; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;">आचार्य भानुभक्तको महान रचना ‘रामायण’ हो। तर उनले नेपाली स्त्री—समाजका निम्ति पनि ‘बधूशिक्षा’ र केही स्फूट कविताहरूको रूपमा कोशेली प्रदान गरेका छन्। हुन ता आधुनिक समालोचकहरूले नवीन दृष्टिकोणद्वारा समीक्षा गर्दा ‘बधूशिक्षा’लाई साधारण रचना घोषित गरेका छन्। उनीहरूले कुनै पनि साहित्यकारको कृतिको मूल्यांकन गर्दा उनले भोगेको सामाजिक अर्थात् तत्कालिक परिवेशलाई पनि ध्यानमा राख्नुपर्दछ।</p><p style="margin-top: 15px; margin-bottom: 15px; text-align: justify; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;">वर्तमान समयमा पनि नेपाल देशका केही प्रसिद्ध सहरहरू बाहेक ग्रामीणवासीहरूमा सामाजिक प्रगतिको गति अझ सामान्य नै छ भने झन् गत शताब्दीको नेपालीहरूको ग्रामीण जनजीवनको अवस्था कुन स्तरमा थियो होला त्यसको कल्पना सहजै गर्न सकिन्छ। भानुभक्तका पालामा ठेट नेपाली समाजको अस्तित्व त अझै पनि नेपालका गाउँहरूतिर पाइन्छ। गत शताब्दीको भारतको अवस्था पनि त साह्रै गतिलो स्तरको थिएन। यस देशका सुधारवादी आन्दोलनको सूत्रपात राजा राममोहनरायद्वारा भइसकेको थियो। उनले आफ्नो अथक परिश्रम दृढ विश्वासले भारतमा सती प्रथा बन्द गराउन १८२९ ईस्वीमा सफलता पाएका हुन्। भारतदेखि हजारौं किलोमिटर दूर्गम पार्वत्य भूमिमा निवास गर्ने र सामान्य साधना भएका भानुभक्तले आखिर भारतमा भइसकेको सामाजिक सुधारका समाचारहरूदेखि कसरी समाचार पाउन सक्थे? यसैकारण १८२९ ईस्वीमा भारतमा सती प्रथा बन्द भइसकेता पनि भानुभक्तले लेखेः</p><p style="margin-top: 15px; margin-bottom: 15px; text-align: justify; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;"><strong>सौताको रिस गर्नु पाप् छ बहुतै दूवै एकैका पति।&nbsp;</strong><br><strong>मर्दामा पनि जानुपर्छ जसले मीलेर सँगै सति।।</strong></p><p style="margin-top: 15px; margin-bottom: 15px; text-align: justify; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;">उक्त पंक्तिहरूद्वारा तत्कालीन नेपालमा चलिरहेको सती प्रथाको समर्थन देखिन्छ। साथै सौताको रिस गर्नु पाप छ भन्नाले पनि कविले बहुपत्नी विवाहको समर्थन गरेको बुझिन्छ।</p><p style="margin-top: 15px; margin-bottom: 15px; text-align: justify; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;">बधूशिक्षा कविको एउटा आकस्मिक रचना हो। यस रचनालाई तयार पार्ने प्रेरणा कवि भानुभक्तले मित्र तारापतिका घरमा आधा रातमा चलेको सासु बुहारीको कलहबाट पाए। यस कृतिमा भानुभक्तले सासुको समर्थन गर्दै बुहारीलाई सरल हिसाबले शिक्षा दिएका छन्। ‘बधूशिक्षा’लेख्ने कविको यही उद्देश्य देखिन्छ– स्त्रीहरू पनि पढ्ने पढाउन सक्ने होउन् समयमा कर्तव्यच्युत नहोउन्, साथै पति परायण सहगृहस्थ जीवनलाई शान्तिपूर्वक निर्वाह गरून् भनेर गृहलक्ष्मीहरूलाई शिक्षा दिएका हुन्।</p><p style="margin-top: 15px; margin-bottom: 15px; text-align: justify; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;">यस पुस्तकमा नेपाली बधूहरूलाई बडो सरल हिसाबले शिक्षा दिइएको छ। हुन त अहिलेको युगमा यस प्रकारको नारी शिक्षा उपयुक्त देखिँदैन। तर समय काल र परिस्थितिअनुसार यो शिक्षा त्यस कालका लागि उपयुक्त नै थियो। गत शताब्दीको कृति हुनाले हामीले वर्तमान युगको दृष्टिकोण लिएर त्यसको मूल्यांकन गर्न उचित हुँदैन, यसो गर्नाले हाम्रो कविप्रति अन्याय हुनेछ। बधू भन्नाले कविले यहाँ सम्पूर्ण नारी जातिलाई लिएका छन् र घरलाई नारी जातिले नै स्वर्ग बनाउँदछन्, सुख र सम्बृद्धि ल्याउन पनि नारीले नै सक्तछन् भन्ने कुरोमा कविको विश्वास छ। एउटी गृहिणीको जुन उत्तरदायित्व छ, यसको कर्तव्य अकर्तव्यप्रति कविले समस्त नारी जातिलाई सचेष्ट गराएका छन्। कुनैपनि सामाजिक परिवारमा एउटी बधूको कत्रो जिम्मेवारी हुन्छ, त्यसैको चर्चा कविले गरेका छन्। यसरी हाम्रा कविले नारीको सम्मान नै गरेका छन्, तिरस्कार होइन।</p><p style="margin-top: 15px; margin-bottom: 15px; text-align: justify; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;">तारापति ब्राह्मणीका पत्नी र बुहारीलाई आचार्य भानुभक्तले गाउँघरको चलनअनुसार र व्यवहार अनुसार जुनप्रकारको मर्यादावादको शिक्षा दिएका छन् त्यो आधुनिक पाश्चात्य सभ्यताका आदर्शभित्र हुर्किएका हाम्रा नवयुवतीहरूलाई कटु र संकीर्ण लागे पनि यो अन्य नारीहरूले रूचाउने कुरो हो। भानुको ‘बधू्शिक्षा’ले आफ्नो जातीय संस्कृतिको मर्यादा बुझाउँछ। नेपाली पहाडी गृहस्थीमा बुहारीहरूबाट यस्तै व्यवहारको अपेक्षा आज पनि गरिन्छ र जबसम्म हाम्रो सामाजिक जीवनमा आमूल परिवर्तन हुँदैन, तबसम्म भानुभक्तको यस व्यवहारपूर्ण मर्यादावादी दृष्टिकोणमा केही अन्तर आउनेछैन।</p><p style="margin-top: 15px; margin-bottom: 15px; text-align: justify; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;">आजका नारीहरू प्रश्न गर्दछन्ः भानुभक्तले तारापतिका घरमा मच्चिएको झगडामा सासुको दोष चाहिँ किन देखेनन्? सासु बुहारीको झगडामा सासुको पनि केही कसूर त हुनुपर्छ, नत्र भने झगडा त्यत्रो किन बढ्ने थियो? बधूवर्गलाई मात्रै शिक्षा दिने, सासुवर्गले बुहारीसित कस्तो प्रकारको व्यवहार गर्नुपर्छ, बुहारीलाई घरकी छोरीको रूपमा नै सासुले मानिलिए के खेरो जाने थियो र? आखिरमा बुहारी पनि त कुनै मातापिताकी अति प्यारी छोरी हो, के बुहारी हुँदैमा ऊ छोरीको पददेखि वञ्चित भइहाल्ने?</p><p style="margin-top: 15px; margin-bottom: 15px; text-align: justify; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;">सासु र बुहारीको झगडा नेपाल देशमा मात्र होइन, अन्य देशहरूमा लगायत भारतमा पनि प्रचलित छ। हो, सहरी हावा लागेका छोराहरू आफ्नी आमा र पत्नीको झगडाको एउटै फैसला गर्दछन्– भिन्नै गृहस्थी बसाएर। नेपालका सहरहरूतिर पनि यस्तै प्रकारको व्यवस्था प्रायः स्थापित भइसकेको छ। यसो हुँदा हाम्रो जुन संयुक्त प्रथा थियो, त्यो भत्किँदै गइरहेछ। तर नेपाल सहरदेखि टाढाटाढा बसेका ग्रामीणहरूमा संयुक्त परिवारको प्रथा अझ छँदैछ। धेरै सदस्यहरू भएका परिवारकी बुहारीमाथि कत्रो जिम्मेवारी आइपर्छ, त्यसको स्पष्ट चित्र भानुभक्तको बधूशिक्षामा पाइन्छ।</p><p style="margin-top: 15px; margin-bottom: 15px; text-align: justify; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;">बधूशिक्षा लेख्ने कविले आफ्नो कृतिमा बधूहरूलाई मात्र उपदेश दिएनन् तर हामीहरूको जीवनमा आश्रित मर्यादापूर्ण व्यवहारको प्रेरणाले हाम्रो सामाजिक व्यवहारलाई पनि उद्बोधन गरेका छन्। उदाहरणका लागि स्त्री जातिलाई पतिव्रता भई धर्मपालन गर्ने उपदेश धेरै नै दिइएको छ। एक प्रकारले यो राम्रै अर्ति हो तर लोग्ने मान्छेको निरंकुशता र मनमानी स्वभावप्रति केही पनि विरोध नगरी उनीहरूले गर्ने बहुविवाहको अन्याय अत्याचारलाई पनि भानुभक्तले समर्थन गरेका छन्, त्यो अवशय नारीलाई नसुहाउँदो कुरा हो। लोग्ने मानिसको यस अन्यायप्रति विरोध नगरी स्वास्नी मानिसहरूले चुप लागेर बस्नाले हुन ता घरको शान्ति अवस्य स्थिर नै रहन्छ, तर नारी जागृति र प्रगतिको दिशामा त यसले सरासर बाधा दिएर समाजको विकास र प्रगतिको बाटो रोक्दछ।</p><p style="margin-top: 15px; margin-bottom: 15px; text-align: justify; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;">पुस्तकको अर्को एक स्थानमा भानुभक्तले नारीलाई हाँस्नेसम्मको पनि अधिकार दिएका छैनन्। हाँस्नु त मानवको एउटा प्रकृतिद्वारा प्रदत्त गुण हो यस नैसर्गीय गुणमाथि पनि कविले केही अंकुश लगाएका छन्। बधूशिक्षामा नै कविले नारीहरूलाई असल बनाउने चिन्तन गरेर पनि उनीहरूलाई माथि उठाउने भावना र केही स्वतन्त्रता दिने उदारता देखाएनन्। यसप्रकार नारीलाई केही विशेष अधिकारहरू नदिँदा समाजमा विकास आउँदैन। हाँस्ने कुरामा नारी जातिलाई अंकुश लाउँदै उनले लेखेका छन्ः</p><p style="margin-top: 15px; margin-bottom: 15px; text-align: justify; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;"><strong>हाँस्नू छैन कदापि नारीहरूले बेश्या हुन्या हाँस्तछन्।&nbsp;</strong><br><strong>बेश्या लौ नहउन् तथापि घरको काम् ती सबै नास्तछन्।&nbsp;</strong><br><strong>एक्लै हाँस्न हुँदैन कोहि नभई अर्को सँगै भो जसै।&nbsp;</strong><br><strong>हाँस्तैमा दिन जान्छ येहि रितले सब् काम बित्दछन् जसै।</strong></p><p style="margin-top: 15px; margin-bottom: 15px; text-align: justify; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;"></p><div class="cs_img " style="max-width: 100%; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px; width: 1000px;"><p style="margin-bottom: 15px;"><img src="https://nepalkhabar.com/get_img?ImageWidth=1000&amp;ImageHeight=700&amp;ImageId=26938" alt="डा. कमला सांकृत्यायन" title="डा. कमला सांकृत्यायन" border="0" style="max-width: 100%; height: auto;"></p><p class="cs_img_caption" style="margin-top: 15px; margin-bottom: 15px; color: rgb(136, 136, 136); font-size: 14px !important;">डा. कमला सांकृत्यायन</p></div><p style="margin-top: 15px; margin-bottom: 15px; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;"></p><p style="margin-top: 15px; margin-bottom: 15px; text-align: justify; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;">बधूशिक्षामा दिइएको उक्त श्लोक लेख्ने पनि कविलाई एउटा कारण परेको थियो। कविले आफ्नी धर्मपत्नीले उनको सामु हाँसिदिँदा उनले यस श्लोकमा पत्नीलाई खसालेर भनेका छन्। आफ्नी धर्मपत्नीमाथि नै उनको यस्तो नियंत्रण थियो भने अन्य नारीहरूप्रति के उनी नरम हुन सक्थे? तर कविलाई हामीले केही नसोची, नसम्झी दोष दिनु उचित हुँदैन। भारतका प्रसिद्ध कवि गोस्वामी तुलसीदासले त झन् नारीहरूप्रति कति तीतो फैसला दिएर गएका छन्—</p><p style="margin-top: 15px; margin-bottom: 15px; text-align: justify; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;"><strong>ढोल, गँवार, शूद्र, पशु, नारी।</strong><br><strong>ये सब ताडन के अधिकारी।।</strong></p><p style="margin-top: 15px; margin-bottom: 15px; text-align: justify; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;">तर तुलसीले यस्तो पंक्ति लेखे भनेर हामीले आधुनिक दृष्टिकोणले कविको तिरस्कार गर्नु हुँदैन। कारण कविले भोगेको यथार्थ र परिस्थितिप्रति पनि हाम्रो दृष्टि जानैपर्छ।</p><p style="margin-top: 15px; margin-bottom: 15px; text-align: justify; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;">भानुभक्तका अन्य फूटकर कविताहरू हेर्दा कविलाई रसिकको रूपमा पनि चिन्न सकिन्छ। नेपाली नारीहरूको पुष्पप्रेमको झलक उनको कान्तिपुरी नामक कवितामा पाइन्छः</p><p style="margin-top: 15px; margin-bottom: 15px; text-align: justify; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;"><strong>चपला अबलाहरू एक् सुरमा।</strong><br><strong>गुनकेसरिको फूल ली शिरमा।।</strong><br><strong>हिँडन्या सखि लीकन ओरिपरी।</strong><br><strong>अमरावति कान्तिपुरी नगरी।।</strong></p><p style="margin-top: 15px; margin-bottom: 15px; text-align: justify; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;">आज पनि नेपाली स्त्रीहरू आफूलाई सिंगार्न आफ्नो इच्छाले मनपर्दो फूल टिपेर कपालमा लगाउँदछन्। झन् भानुभक्तको पालामा तरूणीहरू त कति शौकिन थिए होलान् यस्ता रमणीय पुष्पप्रेमले भानुभक्त पनि चकित भएका थिए।</p><p style="margin-top: 15px; margin-bottom: 15px; text-align: justify; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;">भानुभक्तका फुटकर रचनाहरूमा जुन भावना, विचार र प्रश्नहरू छन् ती मूलतः नेपालीहरूको पारिवारिक जीवनमा भन्दा पृथक छैनन्। यो साँचो हो कि आज गजाधार सोतीकी घरबूढीको प्रसंग लिएर कतिपय सज्जनहरूले भानुको प्रतिवाद गर्न खोजेका छन्। तर स्पष्टवादी कवि आफ्ना भावनाहरूलाई मनैमा गाँठो पारेर राख्न सक्दैनथे। नारीप्रति कटु भावना लिएर उनले यस्तो लेखेको कदापि होइन।</p><p style="margin-top: 15px; margin-bottom: 15px; text-align: justify; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;">नौजवान ठिटाठिटीले कुनै न कुनै बुढीको कच्कच् अवश्य नै सुनेका छन् होला। भानुभक्तले आफ्ना रचनाहरूमा कदाचित् पहिलो पटक यस्तो चोट लगाएका छन् गजाधरकी पत्नीमाथि कटुशब्द भनेर। यस प्रसंगबाट स्पष्ट देखिन्छ, त्यस युगमा वृद्ध सासुहरूलाई आफ्ना तरूणी बुहारीहरूको कति चिन्ता हुन्थ्यो। विशेष गरी घरमा पुरूष सदस्य नभएको बेला कुनै तन्देरी अतिथी आइदिनाले वृद्धालाई हुन सम्मको चिन्ता परेको थियो। कर्कश बुढीले हुन त बुहारीप्रतिको आफ्नो जिम्मेवारीको पालना गरिन्, तर अतिथिको मनमा चोट पुर्‍याएर गइन्।</p><p style="margin-top: 15px; margin-bottom: 15px; text-align: justify; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;">नारीलाई त कविले केही भन्नै सकेनन् तर गजाधर सोतीलाई लेखेर दिएको कवितामा कविले कर्कशा नारीहरूको कटु आलोचना गरेका छन्। (<strong>भानु स्मारिका विशेषांक</strong>, सम्बत् २०३७–३८ (सन् १९८१), प्रकाशक श्री खिरोद मेगी। भाानु जन्म जयन्ती समारोह समिति, मंगन, उत्तर सिक्किम। यो लेख&nbsp;<strong>रचना</strong>पत्रिकाको भानु विशेषांकको पूर्णांक १३१ मा ‘भानुभक्तको रचनामा सामाजिक शिक्षा’ शीर्षकमा प्रकाशित छ।)</p>', '2017-07-20 22:44:53', '2017-07-20 22:44:53'),
(36, 'एक कविको अर्को अध्याय अर्थात् उपमेयर', 'art-literature', '15.PNG', 'one_litrature', '2017-07-20', '<p style="margin-bottom: 15px; text-align: justify; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;">नागरिकताको नाम– ढाकाकुमारी पराजुली। बोलीचालीको नाम– तारा पराजुली, वर्ष ३६। ढाकाकुमारी नाम कसरी रह्यो, त्यो पनि भनिहालौं। एकताका उनको परिवारमा चार पुस्तासम्म लगालग छोरा मात्रै जन्मिए, छोरी जन्मिएनन्। तर, चाहिएको थियो– छोरी।&nbsp;</p><p style="margin-top: 15px; margin-bottom: 15px; text-align: justify; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;">त्यसैक्रममा ताराको जन्म भयो। उनको आगमनसँगै सिंगो परिवारमा नयाँ खुसी छायो। तब हजुरआमाले ‘सबैलाई ढाक्ने गरी जन्मिएकी छोरी’ भन्दै उनको नामै ‘ढाका’ राखिदिइन्। पछि ‘कुमारी’ चाहिँ कसले थपिदियो, ढाकालाई पनि थाहा छैन। समयक्रमसँगै आज उनै ढाकाले आमा–बाबुको चाउरिँदो अनुहारलाई खुसीले छपक्कै ढाकिदिएकी छन्।</p><p style="margin-top: 15px; margin-bottom: 15px; text-align: justify; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;">०३८ सालमा भोजपुरको साबिक ट्याम्केबाट सहरको सुख–सुविधासँग रम्न मोरङको डाँगीहाट आउँदा तारा (ढाका) सँग उति ठूला सपना थिएनन्, न थिए कुनै खास योजना। बसाइँ सराइसँगै उनी गाउँको अन्कन्टारबाट बाहिर निस्किइन्, सहरको क्याम्पसमा आएर उच्च शिक्षाका लागि भर्ना भइन्, सहरीया जीवनशैलीसँग उठबस गर्न थालिन्। यसले के सपना भन्ने?</p><p style="margin-top: 15px; margin-bottom: 15px; text-align: justify; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;">तर, मोरङको डाँगीहाट आएसँगै तारा पराजुलीको जीवनमा नयाँ ‘टर्निङ प्वाइन्ट’चाहिँ अवश्यै आयो। डाँगीहाट कोसी राजमार्गसँगै जोडिएको भए पनि अस्तिसम्म गाउँ नै थियो। भलै, अहिले ‘मिनी सहर’का रूपमा विकास हुँदैछ। त्यही आधा सहर, आधा गाउँजस्तो लाग्ने डाँगीहाटमा घर/ठेगाना सारेर आएसँगै ताराको जीवनमा केही नयाँ पन्ना खुले, खुल्न थाले।</p><p style="margin-top: 15px; margin-bottom: 15px; text-align: justify; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;">त्यो कसरी भने, यता आएपछि उनी पूर्वकै प्रतिष्ठित सुकुना क्याम्पसमा स्नातक तहमा पढ्न थालिन्, कविता लेख्न थालिन् र साहित्यिक गतिविधिमा सक्रिय हुन थालिन्। भोजपुर छँदै अखिलको प्रारम्भिक कमिटी हुँदै एमालेको झन्डामुनि उभिएकी तारा मोरङ आएपछि तत्कालै संगठित नभए पनि परोक्ष रूपमा एमालेका लागि राजनीति गर्न थालिन्, क्याम्पसबाट। पढाइकै क्रममा उनले आफ्नो दायरा बढाउँदै लगिन्।</p><p style="margin-top: 15px; margin-bottom: 15px; text-align: justify; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;"></p><div class="cs_img " style="max-width: 100%; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px; width: 960px;"><p style="margin-bottom: 15px;"><img src="https://nepalkhabar.com/get_img?ImageWidth=960&amp;ImageHeight=496&amp;ImageId=26456" alt="कवि तारा पराजुली" title="कवि तारा पराजुली" border="0" style="max-width: 100%; height: auto;"></p><p class="cs_img_caption" style="margin-top: 15px; margin-bottom: 15px; color: rgb(136, 136, 136); font-size: 14px !important;">कवि तारा पराजुली</p></div><p style="margin-top: 15px; margin-bottom: 15px; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;"></p><p style="margin-top: 15px; margin-bottom: 15px; text-align: justify; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;">समयक्रमसँगै उनले दशकको दौरानमा साबिक डाँगीहाट गाविसको वडा नम्बर ३ को सदस्य, युवा संघ नेपालको कोसी अञ्चल सदस्य, एमाले निकट जनसाहित्य मञ्च मोरङकी जिल्ला सचिवको नेतृत्व सम्हाल्दै उनी अनेमसंघ मोरङको सचिव र अध्यक्ष हुँदै १० वर्ष नेतृत्व सम्हालिन्। त्यसक्रममा उनले समाज पढिन्, महिलाको अवस्था बुझिन्, सामाजिक अन्तर्विरोध तथा अन्तर्वस्तु केलाइन्।</p><p style="margin-top: 15px; margin-bottom: 15px; text-align: justify; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;">त्यसक्रममा ताराले गाउँघरमा नेतृत्वदायी महिलाको भूमिका निभाउन थालिन्। सामाजिक सक्रियता बढाउँदै लगिन्। त्योभन्दा ठूलो कुरा, उनले आफूलाई एक नेतृका रूपमा तयार पार्दै लगिन्। अझ भनौं, उनले नजानिँदो पाराले गाउँबाटै आफ्नो राजनीतिक पृष्ठभूमि तयार पार्दै गइन्। निजी स्कुलमा पढाउने जागिर र कविताकर्म त छँदै थियो।</p><p style="margin-top: 15px; margin-bottom: 15px; text-align: justify; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;">अन्ततः भोजपुरको एउटा ग्रामीण स्कुलबाट उचालेको पाइलाले आज उनै ढाकाकुमारी पराजुलीलाई मोरङको एउटा प्रमुख सहर बेलबारीको उपमेयरको कुर्सीमा ल्याएर उभ्याइदिएको छ। यो सफलताको अघिल्लितर उनले पार गरी आएका थुप्रै उकाली–ओराली छन्।</p><p style="margin-top: 15px; margin-bottom: 15px; text-align: justify; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;">‘यो त मेरो संघर्ष र मेहनतको फल हो,’ उपमेयरमा निर्वाचित भएपछि उनी भन्छिन्, ‘अब त म झन् ठोस जिम्मेवारीसामु छु, सार्वजनिक दायित्व ममाथि छ। यो मेरा लागि अवसर र र चुनौती दुवै हो।’</p><p style="margin-top: 15px; margin-bottom: 15px; text-align: justify; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;">उनले अगाडि थपिन्, ‘म समाजमा महिलालाई ठाउँ छैन भन्नेहरूका लागि म जवाफ बन्न चाहन्छु।’</p><p style="margin-top: 15px; margin-bottom: 15px; text-align: justify; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;">साबिकको बेलबारी र डाँगीहाट गाविस मिलेर बेलबारी नगरपालिका भएसँगै बेलबारी अहिले मोरङका प्रमुख सहरहरूमध्येको यौटा प्रमुख सहर बनेको छ। उक्त नगरमा (नेकपा) एमालेले मेयर र उपमेयर दुवै पदमा बाजी मार्दा ताराको विधिवत् रूपमै नयाँ राजनीतिक उडान सुरू भएको छ। अब उनको यो उडान कहाँसम्म पुग्छ, उनैलाई थाहा होला।</p><p style="margin-top: 15px; margin-bottom: 15px; text-align: justify; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;"></p><div class="cs_img " style="max-width: 100%; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px; width: 816px;"><p style="margin-bottom: 15px;"><img src="https://nepalkhabar.com/get_img?ImageWidth=816&amp;ImageHeight=477&amp;ImageId=26457" alt="कवि तारा पराजुली" title="कवि तारा पराजुली" border="0" style="max-width: 100%; height: auto;"></p><p class="cs_img_caption" style="margin-top: 15px; margin-bottom: 15px; color: rgb(136, 136, 136); font-size: 14px !important;">कवि तारा पराजुली</p></div><p style="margin-top: 15px; margin-bottom: 15px; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;"></p><p style="margin-top: 15px; margin-bottom: 15px; text-align: justify; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;">चुनावका लागि मनोनयन दर्ता गराउने अघिल्लो दिनसम्म पनि ताराले उपमेयरको टिकट पाउने–नपाउनेमा अन्योल नै थियो। तर, पार्टीभित्र गुट–उपगुट मिलाएर उम्मेदवारी दिनुपर्ने बाध्यताबीच अन्ततः एमालेले ढाकालाई उपमेयरका रूपमा अघि सार्ने निर्णय लियो, त्यो पनि अन्तिम घडीमा आएर।</p><p style="margin-top: 15px; margin-bottom: 15px; text-align: justify; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;">त्यसो त ताराको उम्मेदवारी अपेक्षित नै थियो। स्थानीयस्तरमा उनको बराबरी गर्ने वा भनौं चुनाव नजिते पनि प्रतिस्पर्धी मत ल्याउने अरू महिला भएनन्। तब पढेलेखेकी, देशमा युवा कवयित्रीका रूपमा चिनिएकी, जनसम्पर्क राम्रो भएकी तथा सामाजिक छविसमेत राम्रो भएका आधारमा एमालेले उनलाई पत्यायो। र, नगरको उपमेयरको जिम्मा लगायो। तब उनी केही थान नारा बोकेर मैदानमा उत्रिन्। र, माइती गाउँबाटै चुनाव जितिन्।</p><p style="margin-top: 15px; margin-bottom: 15px; text-align: justify; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;">‘मेरो अनुभव, नेतृत्व क्षमता र योग्यताका आधारमा पार्टीले मलाई टिकट दिएको हो,’ ताराको प्रष्टोक्ति छ, ‘मैले टिकटका लागि कहीं कतै पनि लविङ गरिनँ। र, म त्यसो गरेर टिकट पाउने पक्षमा थिइनँ पनि।’</p><p style="margin-top: 15px; margin-bottom: 15px; text-align: justify; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;">जे होस्, ताराले जसरी टिकट पाए पनि पार्टीलाई उनले निराश पारिनन्। उच्च चर्चामा रहेको मोरङमा मतपरिणामको मध्यतिरको रूझानबीच धर्मराउँदै गरेको एमालेलाई बेलबारीबाट ताराको टिमले टेको हाल्यो। र, एमालेको जितको खातामा थप्यो– बेलबारी नगरपालिका। भलै, मोरङमा एमालेको समग्र नतिजा भने निराशाजनक रह्यो।</p><p style="margin-top: 15px; margin-bottom: 15px; text-align: justify; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;">३६ वर्षीया ताराले ढिला गरी घरजम बसालिन्, गत वर्ष असोजमा। पाँचथरका प्रेम सापकोटासँग मागी बिहे गरेकी उनी अहिले माइतीघरमै (साबिकको डाँगी हाट, हालको बेलबारी नगर) छिन्। अमेरिकाको ग्रीन कार्ड होल्डर उनका श्रीमान् अहिले अमेरिकातिरै छन्।</p><p style="margin-top: 15px; margin-bottom: 15px; text-align: justify; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;">तारा आफू आज यो ठाउँसम्म आइपुग्नुको श्रेय एकमुष्ठ रूपमा आमाबुबा तथा घरतर्फका अभिभावलाई दिन्छिन्। संयोग पनि कस्तो भइदियो भने, परिवार तथा बिहे गरेर गएको घरसमेत एमाले पृष्ठभूमिकै। ससुरा त गाउँका नेता नै। तब तारालाई राजनीतिक रूपमा उत्रिन कुनै बाधा झेल्नु परेन। वा भनौं, राजनीति गर्न परिवारसँग विद्रोह गर्नुपरेन।</p><p style="margin-top: 15px; margin-bottom: 15px; text-align: justify; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;">‘बुबाआमा, मेरा श्रीमान् तथा सासू–ससुराले तिमी सक्षम छौं, अघि बढ नभनेको भए आज यो सफलता मसँग हुनेथिएन,’ उनी भन्छिन्, ‘मेरो यो सफलतामा मेरो संघर्षको जति मूल्य छ, परिवारको सपर्मण र हौसला पनि उत्तिकै छ।’</p><p style="margin-top: 15px; margin-bottom: 15px; text-align: justify; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;">गाउँघरमा एक जुझारू युवा नेतृ तथा सामाजिक अभियन्ताको समेत पहिचान बनाएकी ताराको मुख्य पेशा भनेको शिक्षण हो। अन्य स्थानीयसँगको संयुक्त साझेदारीमा दशकअघि स्थापना गरेको ज्ञानोदय बोर्डिङ स्कुलले चार ब्याचका एसएलसी विद्यार्थी निकालिसकेको छ। उनी उक्त स्कुलको लगानीकर्ता र शिक्षक पनि हुन्।</p><p style="margin-top: 15px; margin-bottom: 15px; text-align: justify; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;"></p><div class="cs_img " style="max-width: 100%; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px; width: 816px;"><p style="margin-bottom: 15px;"><img src="https://nepalkhabar.com/get_img?ImageWidth=816&amp;ImageHeight=612&amp;ImageId=26458" alt="कवि तथा बेलबारी नगरपालिकाकी उपमेयर तारा पराजुली" title="कवि तथा बेलबारी नगरपालिकाकी उपमेयर तारा पराजुली" border="0" style="max-width: 100%; height: auto;"></p><p class="cs_img_caption" style="margin-top: 15px; margin-bottom: 15px; color: rgb(136, 136, 136); font-size: 14px !important;">कवि तथा बेलबारी नगरपालिकाकी उपमेयर तारा पराजुली</p></div><p style="margin-top: 15px; margin-bottom: 15px; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;"></p><p style="margin-top: 15px; margin-bottom: 15px; text-align: justify; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;">हिजोअस्तिसम्म कविता लेखेर, कविता सुनाएर राजनीतिक विकृतिविरूद्ध व्यंग्य कस्ने तारा अहिले आफू स्वयं त्यही बाटोमा छन्। उनी यतिबेला नगर हाँक्ने जिम्मेवारीसामु छिन्। ‘म हिजो कविता लेखेर समाज रूपान्तरण गर्ने वा भनौं जागरण पैदा गर्ने भूमिकामा थिएँ,’ उनी योजना सुनाउँछिन्, ‘अब जनताको सेवा र विकासको मोर्चामा औपचारिक रूपमा प्रवेश गरेकी छु। र, यो मेरा लागि एउटा नयाँ अध्याय पनि हो।’</p><p style="margin-top: 15px; margin-bottom: 15px; text-align: justify; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;">अब अलिकति ताराको कविता र सिर्जनाको पनि कुरा गरिहालौं। उनी कविकै रूपमा दरिएको भोजपुरबाट मोरङ आइकनै हो। दशक लामो कविता यात्रामा उनले यौटी वजनदार कवयित्रीको पहिचान बनाएकी छन्। आधा दर्जनजति पुरस्कार पनि खाएकी छन् (स्कुल/कलेजतिर पाएका पुरस्कारको संख्या यहाँ जोडिएको छैन)। भलै, पुरस्कार पाउनु र नपाउनु कुनै कविको मानक हुने हैन।</p><p style="margin-top: 15px; margin-bottom: 15px; text-align: justify; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;">केही थान बालकथाका पुस्तकसमेत प्रकाशन गरिसकेकी ताराका आधा दर्जनजति गीत/गजल पनि रेकर्ड भएका छन्। उनको छिट्टै नै पहिलो कविताकृति आउँदैछ।</p>', '2017-07-20 22:48:12', '2017-07-20 22:48:12'),
(37, 'सिंहले चितुवाको बच्चा स्याहारेपछि', 'entertainment', '16.PNG', 'lion_cured_leopard_child', '2017-07-20', '<p style="margin-bottom: 15px; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;">एक सिंहले अर्को प्रजातिको बच्चा भेटे के गर्ला? तान्जानियामा भने एक सिंहले चितुवाको बच्चा स्याहार गर्दै गरेको दृश्य क्यामरामा कैद भएको छ। यस्तो दृश्य पहिलो पटक देखिएको जनाइएको छ।</p><p style="margin-top: 15px; margin-bottom: 15px; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;">‘नोसिक्तोक’ नामक पाँच वर्षको पोथी सिंहले तान्जानियाको ङोरोङोरो संरक्षण क्षेत्रमा यस्तो अनौठो व्यवहार देखाएको हो।</p><p style="margin-top: 15px; margin-bottom: 15px; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;">सो सिंहलाई नजिकबाट नियालिरहेका मानिसहरुले समेत तीन हप्ताको चितुवाको बच्चा किन र कसरी स्याहार्‍यो भन्ने कुरा थाहा नपाएको बताइएको छ।</p><p style="margin-top: 15px; margin-bottom: 15px; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;">यद्यपि एक संरक्षण अभियन्ता ल्युक हन्टरका अनुसार सिंहले भर्खरै बच्चा पाएको र चितुवाको बच्चासँग उसको बच्चा मिल्दोजुल्दो भएको हुनाले यस्तो भएको हो।</p><p style="margin-top: 15px; margin-bottom: 15px; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;">‘यद्यपि यो एकदमै अनौठो घटना हो,’ उनले भने।</p><p style="margin-top: 15px; margin-bottom: 15px; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;"></p><div class="cs_img " style="max-width: 100%; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px; width: 624px;"><p style="margin-bottom: 15px;"><img src="https://nepalkhabar.com/get_img?ImageWidth=624&amp;ImageHeight=351&amp;ImageId=27069" border="0" style="max-width: 100%; height: auto;"></p></div><p style="margin-top: 15px; margin-bottom: 15px; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;"></p><p style="margin-top: 15px; margin-bottom: 15px; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;">वरिपरि आफ्नो बच्चा नभएको बेला सिंहले चितुवाको बच्चा भेटेको हुनाले उसले स्याहार गरेको किनभने ऊ त्यसबेला मातृत्वको हर्मोनबाट प्रभावित थियो।</p><p style="margin-top: 15px; margin-bottom: 15px; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;">सो बच्चालाई जन्म दिने चितुवाको बारेमा पनि केही जानकारी नभएको बताइएको छ।</p><p style="margin-top: 15px; margin-bottom: 15px; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;">गत मंगलबार सो दृश्य कैद गरिएको हो। तर सिंहले चितुवाको बच्चालाई कतिञ्जेल स्याहार्ने हो, त्यसको कुनै निश्चितता नभएको विज्ञहरुले जनाएका छन्।</p><p style="margin-top: 15px; margin-bottom: 15px; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;">‘सिंहको दम्भले त्यसो गर्न दिन्छजस्तो लाग्दैन,’ हन्टर भन्छन्, सिंहसँग निकै जटिल र उच्च स्तरको सामाजिक सम्बन्ध हुन्छ। उनीहरु हरेकलाई हेरेर र आवाज सुनेर चिन्छन्। त्यसैले उनीहरुले आफ्नो र अरुको बच्चा छुट्याउन माहिर हुन्छन्। यदि त्यसो भएमा चितुवाको बच्चा मारिने सम्भावना बढी छ।’</p><p style="margin-top: 15px; margin-bottom: 15px; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;">तर यदि १२ देखि १८ हप्तासम्म चितुवाको बच्चा बाँच्न सफल भए आफूखुसी बाँच्न सक्ने बताइएको छ।</p><p style="margin-top: 15px; margin-bottom: 15px; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;"></p><div class="cs_img " style="max-width: 100%; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px; width: 624px;"><p style="margin-bottom: 15px;"><img src="https://nepalkhabar.com/get_img?ImageWidth=624&amp;ImageHeight=351&amp;ImageId=27070" border="0" style="max-width: 100%; height: auto;"></p></div><p style="margin-top: 15px; margin-bottom: 15px; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;"></p><p style="margin-top: 15px; margin-bottom: 15px; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;"></p><div class="cs_img " style="max-width: 100%; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px; width: 624px;"><p style="margin-bottom: 15px;"><img src="https://nepalkhabar.com/get_img?ImageWidth=624&amp;ImageHeight=351&amp;ImageId=27071" border="0" style="max-width: 100%; height: auto;"></p></div><p style="margin-top: 15px; margin-bottom: 15px; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;"></p><p style="margin-top: 15px; margin-bottom: 15px; font-family: &quot;Ek Mukta&quot;, sans-serif; font-size: 20px;">एजेन्सी</p>', '2017-07-20 22:51:59', '2017-07-20 22:51:59');
